USE `bxs`;

/*Table structure for table `t_article` */

DROP TABLE IF EXISTS `t_article`;

CREATE TABLE `t_article` (
  `id` varchar(36) NOT NULL COMMENT '主键',
  `article_type` varchar(10) DEFAULT NULL COMMENT '文章类型(1:普通 2:图片 3：视频)',
  `topic_id` varchar(36) DEFAULT NULL COMMENT '栏目ID',
  `article_title` varchar(200) NOT NULL COMMENT '文章标题',
  `article_image_url` varchar(1000) DEFAULT NULL COMMENT '文章配图地址',
  `article_content` text COMMENT '文章内容',
  `publish_dept_id` varchar(36) DEFAULT NULL COMMENT '发布部门ID',
  `publish_user_id` varchar(36) DEFAULT NULL COMMENT '发布人ID',
  `check_state` varchar(10) DEFAULT NULL COMMENT '文章审核状态(0:未审核 1:审核通过)',
  `top_count` bigint(20) DEFAULT NULL COMMENT '推荐值（值越大，越先显示）',
  `view_count` bigint(20) DEFAULT NULL COMMENT '浏览次数',
  `display_order` bigint(10) DEFAULT NULL COMMENT '排序',
  `data_state` varchar(10) NOT NULL COMMENT '数据状态（0：删除 1：正常）',
  `create_date` datetime DEFAULT NULL COMMENT '创建时间',
  `update_date` datetime DEFAULT NULL COMMENT '更新时间',
  `front_slider_state` varchar(10) DEFAULT NULL COMMENT '首页推荐',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

/*Data for the table `t_article` */

insert  into `t_article`(`id`,`article_type`,`topic_id`,`article_title`,`article_image_url`,`article_content`,`publish_dept_id`,`publish_user_id`,`check_state`,`top_count`,`view_count`,`display_order`,`data_state`,`create_date`,`update_date`,`front_slider_state`) values ('06cb3be3-4eeb-4808-8a06-9e31e373530b','1','67a3dde9-92a3-476b-804b-44b1ea100013','加强基层公安队伍建设的六个着力点-','','<p><br/> 　　当前，面对新形势、新常态，基层民警队伍担负的工作任务越发艰巨繁重，工作压力大、思想负担重，导致部分民警工作积极性受到影响，一定程度上制约了公安工作的有效开展。如何提升基层民警队伍精气神，深度挖掘民警潜能，实现工作效能的最大化和最优化，是当前需要认真研究和亟待解决的重要课题。<br/><br/>　　<strong>要抓领头治警先治长。</strong>所谓抓领头就是抓领导班子建设，加大整庸治懒的力度，切实改进领导班子作风，充分发挥领导干部的模范带头作用。打铁还需自身硬，领导干部在以高标准要求别人的同时，更要以高标准要求自己，通过表率作用无声地影响普通民警。<br/><br/>　</p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,0,0,'1','2018-02-05 21:18:36','2018-02-05 21:18:36','0'),('108cfa3b-1c71-4e5e-8176-2cf53064748a','1','f418246f-189b-4dc7-a6d1-b2155f56d23c','机动车驾驶证业务工作规范','','<p><span style=\"color: rgb(51, 51, 51); font-family: 宋体; background-color: rgb(255, 255, 255);\">&nbsp;</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">第一章 总 则</span><br/><span style=\"color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp; 第二章 办理机动车驾驶证申领业务</span><br/><span style=\"color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 第一节 办理初次申领业务</span><br/><span style=\"color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 第二节 办理增加准驾车型申领业务</span><br/><span style=\"color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 第三节 办理持军队、武装警察部队机动车驾驶证申领业务</span><br/><span style=\"color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 第四节 办理持境外机动车驾驶证申领业务</span><br/><span style=\"color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp; 第三章 办理换证、补证和注销业务</span><br/><span style=\"color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 第一节 办理换证业务</span><br/><span style=\"color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 第二节 办理补证业务</span><br/><span style=\"color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 第三节 办理注销业务</span><br/><span style=\"color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp; 第四章 办理记分和审验业务</span><br/><span style=\"color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp; 第五章 档案管理</span><br/><span style=\"color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp; 第六章 考试要求</span><br/><span style=\"color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp; 第七章 附 则&nbsp;</span></span></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,0,0,'1','2018-02-04 14:03:19','2018-02-04 14:03:19','0'),('1da139c4-029d-4f0f-9f02-d7ebbcdc0823','1','17e4c20a-1e6d-43bd-bc4b-11fa36a81df7','市局党员民警赴东苑社区积极开展清扫积雪活动','','<p style=\"padding: 0px;margin-top: 0px;margin-bottom: 0px;font-family: &#39;Microsoft YaHei&#39;;font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255);text-indent: 32px;line-height: 37px\"><span style=\"padding: 0px;font-size: 16px\">1月24日以来我市连降暴雪，为确保小区道路畅通，根据市直机关工委的统一安排，我局机关党委第一时间联系了共建单位东苑社区。1月28日下午3时，市局党委委员、政治部主任、机关党委书记叶红率机关党员民警志愿者40余人，与社区工作人员一起开展积雪清除行动。</span></p><p style=\"padding: 0px;margin-top: 0px;margin-bottom: 0px;font-family: &#39;Microsoft YaHei&#39;;font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255);text-indent: 32px;line-height: 37px\"><span style=\"padding: 0px;font-size: 16px\">志愿者们在刺骨寒风中不顾寒冷与辛苦，满腔热情，手拿铁锹、扫把等除雪工具，积极参加除雪行动。在大家的共同努力下，小区主干道积雪很快就被清除干净，给社区居民创造了一个畅通安全的出行环境。社区雪地里那一个个忙碌的志愿者身影，在寒冬中悄悄温暖了社区群众的心。</span></p><p style=\"text-align: center;\"><img src=\"/media-data/ueditor/image/20180205/1517835037787057955.jpg\" title=\"1517835037787057955.jpg\" alt=\"tst.jpg\"/></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,0,0,'1','2018-02-05 20:50:43','2018-02-05 20:50:43','0'),('1dd62f64-fafe-429b-af45-ba5acca04c36','1','5a04f14f-4d6f-435a-a6ac-393a2f6e8d11','中央巡视再出发：力度不减，节奏不变，新意不少','','<p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">“要深入学习贯彻习近平新时代中国特色社会主义思想和党的十九大精神，贯彻落实十九届中央纪委二次全会部署，运用好党的十八大以来巡视工作成功经验，在坚持中深化、在深化中发展，扎扎实实做好十九届中央巡视工作。”</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　2月2日，中共中央政治局常委、中央巡视工作领导小组组长赵乐际在中央巡视工作动员部署会上，对十九届中央第一轮巡视工作发出动员令。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　这是十九届中央巡视的开篇之作，也是十八大以来巡视工作向党和人民交出优异答卷后的再次出发。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　站在新起点，放眼新征程。今后五年巡视工作将怎样发力，又将如何巩固深化、创新发展？本轮巡视注定备受关注。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　力度不减，节奏不变，新意不少</strong></p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　“中央巡视又启动了！”</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　2月2日晚，十九届中央第一轮巡视动员的消息甫一发布，便引爆大家的“朋友圈”，不少网友边转发边“点赞”，个中期许溢于言表。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　在十八届中央巡视工作圆满完成一届任期全覆盖任务、取得重大战果的基础上，十九届中央巡视重整行装再出发。此时，距离党的十九大闭幕不过三月有余，而距离十九届中央纪委二次全会作出工作部署更是不到一个月时间。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　本轮巡视以习近平新时代中国特色社会主义思想和党的十九大精神为指引，充分吸收运用十八大以来巡视工作的成功经验，同时也颇具新意。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　以被巡视对象为例，本轮巡视打破十八届中央巡视“板块轮动”的惯例，30个被巡视对象中既有地方，也有中央国家机关，还有中央企业。值得一提的是，本轮巡视还根据《中国共产党巡视工作条例》，首次把被巡视地区的副省级城市党委和人大常委会、政府、政协党组主要负责人，一并纳入巡视范围。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　党的十八大以来，随着巡视实践发展，党中央两次修订修改巡视工作条例，及时将政治巡视、一届任期内巡视全覆盖、中央和国家机关巡视、市县巡察等实践创新成果固化为制度，体现了与时俱进的理论品格和实事求是的科学精神。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　记者注意到，本轮巡视不以巡视对象上一次被巡视的“时间远近”为标准，可谓出其不意。比如，本轮将被巡视的河南、住房和城乡建设部，均为2016年十八届中央第十轮巡视的对象。其中，河南已是4年内第三次被中央巡视。这也充分说明了巡视不是“一巡了事”，要确保震慑常在。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　在巡视方式上，本轮巡视虽然沿用十八届中央专项巡视“一托二”的做法，但仍采取常规巡视的方式，时间则由2个月延长至3个月。常规巡视“打头阵”，专项巡视、“机动式”巡视、“回头看”择机而动，这样的战术组合令人期待。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　牢牢把握政治巡视定位</strong></p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　巡视是全面从严治党重大举措、党内监督战略性制度安排。作为党的十九大后的首轮巡视，本轮巡视在一定意义上也可谓十九届中央巡视工作的风向标。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　“做好新时代巡视工作，要牢牢把握政治巡视定位，自觉坚持以习近平新时代中国特色社会主义思想为指导，坚决维护习近平总书记党中央的核心、全党的核心地位，坚决维护党中央权威和集中统一领导。”动员部署会上，赵乐际就坚定不移深化政治巡视提出明确要求。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　政治巡视是十八届中央巡视工作重大理论和实践创新成果之一，明确了“巡视是对党组织和党员领导干部的巡视，是政治巡视不是业务巡视”。去年7月修改的《中国共产党巡视工作条例》，也明确将政治巡视纳入其中。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　从实践来看，十八大以来的巡视工作坚持把政治纪律和政治规矩摆在首位，对照“七个有之”“五个必须”，着力查找被巡视党组织和党员领导干部在政治忠诚、政治担当、政治生态等方面存在的深层次问题。中央巡视发现党的领导弱化、党的建设缺失、全面从严治党不力等方面问题8200余个，省区市和中央单位巡视发现相关问题5.2万余个，巡视的“政治体检”作用得到充分发挥。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　在总结十八大以来巡视工作经验的基础上，党的十九大报告指出，深化政治巡视，坚持发现问题、形成震慑不动摇，建立巡视巡察上下联动的监督网。新修改的党章专列一条，对巡视工作作出明确规定。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　如何贯彻落实十九大精神，坚定不移深化政治巡视？本轮动员部署会上亦给出了答案——</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　要深入监督检查各级党组织和党员领导干部践行“四个意识”、贯彻党章和党的十九大精神情况，保证全党统一意志、统一行动、步调一致前进。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　要坚持在党的全面领导上聚神、加强党的建设上聚力、推进全面从严治党上聚焦，深入监督检查党的政治建设、思想建设、组织建设、作风建设、纪律建设和夺取反腐败斗争压倒性胜利等情况……</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　开展巡视工作，发现问题是生命线、推动解决问题是落脚点。由此看来，围绕政治建设、思想建设、组织建设、作风建设、纪律建设和夺取反腐败斗争压倒性胜利等方面，发现被巡视单位存在的突出问题，将成为本轮乃至十九届中央巡视工作的重要着力点。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　一刻不停歇，以永不懈怠的精神状态推进巡视工作</strong></p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　动员部署会前一天，副局级巡视专员陶建萍一早来到巡视组报到，随即投入到紧张的学习和准备工作中。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　作为全程见证十八届中央12轮巡视的“老兵”，陶建萍觉得，党的十八大以来，中央巡视工作的脚步从未放缓，甚至可以说是一刻不停歇。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　宝剑锋从磨砺出。十八届中央巡视了277个党组织，在党的历史上首次实现一届任期内巡视全覆盖，对16个省区市开展“回头看”，对4个中央单位进行“机动式”巡视。中央纪委审查的案件中，超过60%的线索来自巡视。巡视的力度和效果不断增强，利剑作用彰显。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　如今，在距离春节不到半个月之际，新一轮中央巡视又重整行装再出发。这是贯彻落实十九大精神，将全面从严治党不断引向深入的使命召唤，更是不忘初心、牢记使命的责任担当。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　“我们这些巡视‘老兵’，将牢记肩上的责任和使命，以对党的绝对忠诚，以永远在路上的执着，做好新一轮中央巡视工作。”即将第十三次“出征”的中央第九巡视组副组长兼联络员李博告诉记者，他们正组织全组同志集中学习十九大报告、新修改的党章等重要文件，准确把握中央对巡视工作尤其是深化政治巡视的要求。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　来自审计署的李全中，这两天正如饥似渴地研究《巡视工作有关文件制度汇编》。和李全中一样，中央巡视组有三分之二的干部是从审计、财政、信访等部门抽调的。能够参与首轮巡视，他们在“倍感光荣”的同时，更有紧迫的“本领恐慌”。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　面对再出发，各巡视组明确把加强政治学习、锤炼过硬本领作为一项政治任务，专门安排时间集中学习巡视工作相关制度文件，并要求“老兵”做好传帮带，帮助新同志尽快熟悉业务、迅速进入状态。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　“要政治过硬，也要本领高强，还要以更高的标准、更严的纪律要求自己，做到让党放心、人民信赖。”陶建萍说。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　如其所言，巡视工作担负着特殊使命和重大政治责任，必须按照信念过硬、政治过硬、责任过硬、能力过硬、作风过硬的要求，忠诚于党的信仰，忠诚于以习近平同志为核心的党中央，打造让党放心、人民信赖的高素质专业化巡视队伍，不断加强政治建设、能力建设、作风建设，从严监督管理，确保打铁自身硬。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　“我们将严守纪律要求，依规依纪巡视，发扬优良作风，以永不懈怠的精神状态推进工作，练就敢于担当的‘铁肩膀’，向党和人民交出满意答卷。”李全中表示。</p><p><br/></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,0,0,'1','2018-02-05 19:46:07','2018-02-05 19:46:07','0'),('201fb114-5c78-47c3-af72-8259c60f5e4f','1','dec65f55-feb3-4ef4-909d-1022261285fc','郭声琨主持召开中央政法委员会全体会议','','<p style=\"font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center; text-indent: 2em; margin-bottom: 1em; font-size: 18px;\"><span style=\"font-family: &quot;Times New Roman&quot;;\">1</span>月<span style=\"font-family: &quot;Times New Roman&quot;;\">26</span>日，中共中央政治局委员、中央政法委书记郭声琨在北京主持召开中央政法委员会全体会议并讲话。</p><p style=\"font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; margin-bottom: 1em; font-size: 18px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p style=\"font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; margin-bottom: 1em; font-size: 18px;\">中共中央政治局委员、中央政法委书记郭声琨<span style=\"font-family: &quot;Times New Roman&quot;;\">1</span>月<span style=\"font-family: &quot;Times New Roman&quot;;\">26</span>日主持召开中央政法委员会全体会议，深入学习贯彻习近平总书记在党的十九届二中全会上的重要讲话和全会精神。</p><p style=\"font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; margin-bottom: 1em; font-size: 18px;\">会议指出，习近平总书记在全会上的重要讲话，为我们在新时代推进全面依法治国提供了根本遵循。全会审议通过了《中共中央关于修改宪法部分内容的建议》，把党的十九大确定的重大理论观点和重大方针政策特别是习近平新时代中国特色社会主义思想载入国家根本法，对于更好发挥宪法在新时代坚持和发展中国特色社会主义中的重大作用，具有重要意义。各级政法机关要牢固树立“四个意识”，深刻领会宪法修改的重大意义，切实把思想和行动统一到党中央的重大决策部署上来，始终同以习近平同志为核心的党中央保持高度一致，自觉做宪法的忠实崇尚者、自觉遵守者、坚定捍卫者。</p><p style=\"font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; margin-bottom: 1em; font-size: 18px;\">会议强调，政法机关要带头学习宣传宪法、尊崇实施宪法，坚持严格执法、公正司法，保障宪法法律正确实施，切实维护宪法权威。要结合国家机关“谁执法谁普法”普法责任制，深入开展尊崇宪法、学习宪法、遵守宪法、维护宪法、运用宪法的宣传教育，引导人民群众发自内心拥护、信仰宪法。</p><p style=\"font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; margin-bottom: 1em; font-size: 18px;\">中央政法委委员周强、曹建明、赵克志、汪永清、陈文清、张军、陈训秋、宋丹、黄明出席会议。（图：郝帆）</p><p><br/></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,1,0,'1','2018-02-03 22:32:02','2018-02-03 22:32:02','0'),('21e86236-820a-4500-ba3d-2a516565be2b','2','e89a7d96-d7b5-4840-8e0d-bbdcbb6d4b98','最美交警靓丽街头',NULL,'<p style=\"text-align: center;\"><img src=\"/media-data/ueditor/image/20180205/1517797882346048148.jpg\" title=\"1517797882346048148.jpg\" alt=\"jj2.jpg\"/></p>',NULL,NULL,'0',0,0,0,'1','2018-02-05 10:31:27','2018-02-05 10:31:27','0'),('2be2c34d-f387-47e2-8aef-d63629e82617','1','6bea8a78-4c27-45c5-92f7-cef4679d2479','关于召开城区交通秩序综合整治动员大会的通知','','<p>						</p><p style=\"margin-top: 0px; margin-bottom: 0px; margin-left: 0pt; padding: 0px; color: rgb(51, 51, 51); font-family: 微软雅黑, 宋体, 黑体, &quot;Helvetica Neue&quot;, Tahoma, Arial, &quot;Hiragino Sans GB&quot;; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">镇各道安领导小组成员、各道安成员单位负责人：</p><p style=\"margin-top: 0px; margin-bottom: 0px; margin-left: 0pt; padding: 0px; color: rgb(51, 51, 51); font-family: 微软雅黑, 宋体, 黑体, &quot;Helvetica Neue&quot;, Tahoma, Arial, &quot;Hiragino Sans GB&quot;; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">为加强道路交通安全学习，提高交通安全意识，召开道路交通安全会议，现将有关事项通知如下：</p><p style=\"margin-top: 0px; margin-bottom: 0px; margin-left: 0pt; padding: 0px; color: rgb(51, 51, 51); font-family: 微软雅黑, 宋体, 黑体, &quot;Helvetica Neue&quot;, Tahoma, Arial, &quot;Hiragino Sans GB&quot;; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">一、会议时间：2018年3月1日上午9:15，会期半天。</p><p style=\"margin-top: 0px; margin-bottom: 0px; margin-left: 0pt; padding: 0px; color: rgb(51, 51, 51); font-family: 微软雅黑, 宋体, 黑体, &quot;Helvetica Neue&quot;, Tahoma, Arial, &quot;Hiragino Sans GB&quot;; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">二、会议地点：镇六楼会议室。</p><p style=\"margin-top: 0px; margin-bottom: 0px; margin-left: 0pt; padding: 0px; color: rgb(51, 51, 51); font-family: 微软雅黑, 宋体, 黑体, &quot;Helvetica Neue&quot;, Tahoma, Arial, &quot;Hiragino Sans GB&quot;; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">三、会议内容</p><p style=\"margin-top: 0px; margin-bottom: 0px; margin-left: 0pt; padding: 0px; color: rgb(51, 51, 51); font-family: 微软雅黑, 宋体, 黑体, &quot;Helvetica Neue&quot;, Tahoma, Arial, &quot;Hiragino Sans GB&quot;; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">1、研判辖区道安工作；</p><p style=\"margin-top: 0px; margin-bottom: 0px; margin-left: 0pt; padding: 0px; color: rgb(51, 51, 51); font-family: 微软雅黑, 宋体, 黑体, &quot;Helvetica Neue&quot;, Tahoma, Arial, &quot;Hiragino Sans GB&quot;; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">2、布置辖区道路安全整治工作；</p><p style=\"margin-top: 0px; margin-bottom: 0px; margin-left: 0pt; padding: 0px; color: rgb(51, 51, 51); font-family: 微软雅黑, 宋体, 黑体, &quot;Helvetica Neue&quot;, Tahoma, Arial, &quot;Hiragino Sans GB&quot;; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">3、布置黄标车清理工作；</p><p style=\"margin-top: 0px; margin-bottom: 0px; margin-left: 0pt; padding: 0px; color: rgb(51, 51, 51); font-family: 微软雅黑, 宋体, 黑体, &quot;Helvetica Neue&quot;, Tahoma, Arial, &quot;Hiragino Sans GB&quot;; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">4、布置年终考核工作。</p><p style=\"margin-top: 0px; margin-bottom: 0px; margin-left: 0pt; padding: 0px; color: rgb(51, 51, 51); font-family: 微软雅黑, 宋体, 黑体, &quot;Helvetica Neue&quot;, Tahoma, Arial, &quot;Hiragino Sans GB&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: right; text-indent: 2em;\"><span style=\"text-indent: 2em;\">马鞍山人民政府 &nbsp; &nbsp;</span><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: 微软雅黑, 宋体, 黑体, &quot;Helvetica Neue&quot;, Tahoma, Arial, &quot;Hiragino Sans GB&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: right; text-indent: 2em;\">2017年11月18日</p><p><br/></p><p>\r\n					</p>',NULL,NULL,'0',0,0,0,'1','2018-02-04 10:57:42','2018-02-04 10:58:04','0'),('3435681e-2a0a-4c58-82e7-9054656ac4c4','1','9d4540cb-4457-4642-8359-2449c7326049','交通安全隐患每日排查情况汇报','','<p>&nbsp; &nbsp; &nbsp; &nbsp;<span style=\"color: rgb(51, 51, 51); font-family: PingFangSC-light; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">交通预联办协调交警中队、交管所对辖区的市政道路设施、汽车站、交通运输企业、客运车辆、泥头车等交通安全隐患进行排查，经排查，未发现交通安全隐患。</span></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,1,0,'1','2018-02-05 19:28:41','2018-02-05 19:28:41','0'),('3548d806-da66-42a6-af05-3e1a3d0bd47d','1','0baa5f76-e9e5-40a0-8b76-0223681fe1d0','交警部门频繁行动 多名酒驾者被查获','','<p style=\"transition: color 0.3s linear, background-color 0.3s linear, border-color 0.3s linear, transform 0.3s linear; margin-top: 0.5em; margin-bottom: 0.5em; font-family: simsun; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">进入2018年，我市交警部门继续深入开展道路交通秩序大整治工作，频繁开展查处行动，多名酒驾者心存侥幸上路，被交警查获。</p><p style=\"transition: color 0.3s linear, background-color 0.3s linear, border-color 0.3s linear, transform 0.3s linear; margin-top: 0.5em; margin-bottom: 0.5em; font-family: simsun; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp; 近日，交警三大队民警在水机桥附近开展酒驾查处行动时，一辆黑色轿车在距离检查点一段距离时突然停车，形迹十分可疑。民警随即上前，准备实施拦截。</p><p style=\"transition: color 0.3s linear, background-color 0.3s linear, border-color 0.3s linear, transform 0.3s linear; margin-top: 0.5em; margin-bottom: 0.5em; font-family: simsun; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp; 该车驾驶人见民警走来，下车撒腿就跑。民警迅速展开包抄。该驾驶人慌不择路，逃至附近一小区楼道内。民警随即每个楼道逐一排查，最终在一楼道六层将此人控制。然而，此人以串门儿为借口拖延。民警欲将其带回大队核对信息，当行至一个拐角处时，此人挣脱控制，再次逃窜。民警尾随其后，再次将其控制。经酒精测试仪检测，该男子的酒精含量为35.4mg/100ml，属于饮酒后驾驶机动车。</p><p style=\"transition: color 0.3s linear, background-color 0.3s linear, border-color 0.3s linear, transform 0.3s linear; margin-top: 0.5em; margin-bottom: 0.5em; font-family: simsun; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp; 现场，民警依法将车辆扣留，并对其处以1000元罚款、记12分，暂扣驾驶证6个月。</p><p><br/></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,0,0,'1','2018-02-04 14:12:10','2018-02-04 14:12:10','0'),('39baf2bd-86ac-48b1-8f60-d9182523aa7c','1','9a93fa99-2f13-42e6-97f3-9f04b659c5bd','安徽省公安厅关于报送2017年推进依法行政工作情况的函','','<h1 style=\"box-sizing: border-box; font-size: 22px; font-weight: 400; font-family: 微软雅黑; margin: 0px; color: rgb(14, 118, 205); text-align: center; white-space: normal; background-color: rgb(255, 255, 255);\"><br/></h1><p style=\"box-sizing: border-box; font-family: 宋体; text-indent: 0cm; white-space: normal; background-color: rgb(255, 255, 255); text-align: justify; margin: 0cm 0cm 0pt; font-size: 16pt;\"><span style=\"box-sizing: border-box; font-family: 仿宋_GB2312;\">省政府法制办：</span></p><p style=\"box-sizing: border-box; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: justify; text-indent: 32pt; margin: 0cm 0cm 0pt; font-size: 16pt;\"><span style=\"box-sizing: border-box;\">2017</span><span style=\"box-sizing: border-box; font-family: 仿宋_GB2312;\">年，我厅深入贯彻落实党的十八届四中、五中、六中全会，党的十九大和习近平总书记系列重要讲话精神，紧紧围绕中办、国办印发的《关于深化公安执法规范化建设的意见》和省委、省政府制定的《安徽省法治政府建设实施方案》，以深化公安执法规范化建设为载体，以维护社会公平正义、提升群众安全感和满意率为目标，以强化执法理念、执法能力、执法制度、执法管理建设为重点，精心组织，周密部署，我省公安机关推进依法行政建设法治政府工作取得新进展新成效。</span></p><p><br/></p>',NULL,NULL,'0',0,11,0,'1','2018-02-04 13:53:49','2018-02-04 13:53:49','0'),('42a9b242-faf6-4403-8e5e-ae57beeb932d','1','85627160-b3a8-423f-8648-e2bdccbbacae','人民日报“声音”：新时代公安工作要有新担当新作为','/media-data/image/20180205154623/jj5.jpg','<p>						</p><p style=\"font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; margin-bottom: 1em; font-size: 18px; text-align: center;\"><img src=\"/media-data/ueditor/image/20180203/1517668111001072426.jpg\" title=\"1517668111001072426.jpg\" alt=\"5990441.jpg\"/></p><p style=\"font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; margin-bottom: 1em; font-size: 18px;\"><br/></p><p style=\"font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; margin-bottom: 1em; font-size: 18px;\">近日，习近平总书记就政法工作作出重要指示，对新时代政法工作提出明确要求，为我们做好新时代公安工作指明了前进方向、提供了根本遵循。</p><p style=\"font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; margin-bottom: 1em; font-size: 18px;\">中国特色社会主义进入新时代，我国社会主要矛盾已经转化为人民日益增长的美好生活需要和不平衡不充分的发展之间的矛盾。这意味着世情国情发生了新变化、党和人民提出了新要求，公安工作进入了新的发展阶段、呈现出新的阶段性特征，必须要有新担当新作为。我们要深入贯彻党的十九大精神，坚持以习近平新时代中国特色社会主义思想为指导，积极适应新时代面临的新形势新任务新要求，奋力开创公安工作新局面。</p><p style=\"font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; margin-bottom: 1em; font-size: 18px;\">做好新时代公安工作，必须牢牢把握“两个坚持”，即坚持党对公安工作的绝对领导，坚持以人民为中心的发展思想。这就要求我们，强化“四个意识”，坚持“公安姓党”，做到忠诚核心、拥戴核心、维护核心、捍卫核心，始终在思想上政治上行动上同以习近平同志为核心的党中央保持高度一致；牢记人民公安为人民的初心和使命，大力推进平安中国、法治公安建设，坚持严格规范公正文明执法，为决胜全面建成小康社会、全面建设社会主义现代化国家营造安全的政治环境、稳定的社会环境、公正的法治环境、优质的服务环境，切实担负起为新时代中国特色社会主义保驾护航、做新时代中国特色社会主义事业建设者捍卫者的重大职责使命。</p><p style=\"font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; margin-bottom: 1em; font-size: 18px;\">做好新时代公安工作，必须紧紧围绕“两个增强”，即增强公安工作的预见性、主动性，增强人民群众的获得感、幸福感、安全感。这就要求我们，切实增强忧患意识，强化底线思维，着力打好防范化解重大风险攻坚战，牢牢把握公安工作主动权；按照高质量发展要求，努力为人民群众提供更多更高质量的公共服务产品。群众利益无小事，小案再小，对老百姓来说也是大事，各级公安机关要坚决扭转重大案轻小案倾向，下大力气整治群众反映强烈的治安突出问题，做到更快地破大案、更多地破小案、更好地控发案。坚持以人民为中心的发展思想，讲究的就是一个“实”字，必须切实把这一思想贯彻落实到各项公安工作中，真正把为民造福的事情办好办实，努力让人民的获得感、幸福感、安全感更加充实、更有保障、更可持续。</p><p style=\"font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; margin-bottom: 1em; font-size: 18px;\">做好新时代公安工作，必须着力聚焦“两个深化”，即深化公安改革，深化智能化建设。这就要求我们，大力实施改革强警战略，着眼塑造公安未来，积极推进一场以提升效能为核心、以服务基层为重点的警务体制变革，着力解决影响公安机关战斗力和基层活力的体制性障碍、结构性矛盾、深层次问题；大力实施公安大数据战略，把大数据作为公安工作创新发展的大引擎，着力打造数据警务、建设智慧公安，全面推动公安工作质量变革、效率变革、动力变革，不断提升公安机关的核心战斗力。</p><p style=\"font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; margin-bottom: 1em; font-size: 18px;\">做好新时代公安工作，必须坚持抓发展、抓稳定两手都要硬。要始终坚持稳中求进工作总基调，坚持稳字当头、积极主动作为，努力为推进改革发展创造安全稳定的社会环境。要坚持一手抓当前、一手谋长远，在确保当前我国社会大局持续安全和稳定的同时，扎实抓好那些打基础管全局利长远的事情，做到抓铁有痕、踏石留印，善始善终、善作善成，努力为公安事业长远发展奠定坚实根基。</p><p style=\"font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: right; text-indent: 2em; margin-bottom: 1em; font-size: 18px;\">——公安部党委书记、部长赵克志<span style=\"font-family: &quot;Times New Roman&quot;;\">1</span>月<span style=\"font-family: &quot;Times New Roman&quot;;\">24</span>日在全国公安厅局长会议上说</p><p><br/></p><p>\r\n					</p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,0,0,'1','2018-02-03 22:29:24','2018-02-05 15:46:26','1'),('4496ae1b-09bb-4386-af56-052e57bd5268','1','67a3dde9-92a3-476b-804b-44b1ea100013','聚焦公安队伍建设：夯实思想根基 铸造忠诚警魂','','<p><span style=\"font-family: monospace; font-size: medium; white-space: pre-wrap;\">一个人，要有精气神；一支队伍，亦然。</span></p><p><span style=\"font-family: monospace; font-size: medium; white-space: pre-wrap;\"> &nbsp; &nbsp;公安队伍的建设，铸造忠诚警魂是根本。时代进步、社会发展，公安队伍建设如何积极适应新形势、新要求，成为全警面临的重要考题。</span></p><p><span style=\"font-family: monospace; font-size: medium; white-space: pre-wrap;\">党的十八大以来，全国公安机关认真贯彻落实党中央、国务院的部署安排，深入开展各类主题教育活动，紧密结合公安队伍存在的各类问题，不断夯实为民务实的思想根基。</span></p><p><span style=\"font-family: monospace; font-size: medium; white-space: pre-wrap;\">　一场场触及灵魂的精神洗礼，一次次抓铁留痕的作风锤炼。各级公安机关在铸造忠诚警魂上积极探索，切实把忠诚为民的优良作风根植在民警心中。</span><span style=\"font-family: monospace; font-size: medium; white-space: pre-wrap;\">坚定</span><span style=\"font-family: monospace; font-size: medium; white-space: pre-wrap;\">主心骨</span><span style=\"font-family: monospace; font-size: medium; white-space: pre-wrap;\">不断夯实为民务实的思想根基</span></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,0,0,'1','2018-02-05 21:16:15','2018-02-05 21:16:15','0'),('4bcd2f8e-e693-478b-9d3a-71b5ee0aed23','1','a8d87d32-ec88-4e2e-8a59-1536e9535ac3','全市103个在建工地冰雪天气未现险情','','<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">马鞍山建管部门针对持续低温雨雪冰冻天气，迅速落实“四到位”措施，筑牢建筑安全。截至目前，全市103个在建工地总体平稳可控，未出现一例险情。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">“无论什么时候，都把建筑安全摆在第一位。”2月2日上午，市建管处负责人介绍说，为应对入冬以来强度最大、范围最广和持续最久的低温雨雪冰冻天气，市建管处迅速行动，全面部署，采取“四到位”措施，保障全市建筑工地安全。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">宣传预警到位。通过网络、手机等信息平台及时发布预警信息，引导全市各施工企业及在建工地项目部，必须提前落实应对低温雨雪冰冻天气的各项安全防范措施；隐患排查到位。督促各施工企业及项目部，必须全面开展在建工地各类安全隐患排查工作，重点排查大跨度钢结构厂房、活动板房、料棚等临时设施、深基坑、脚手架及大型起重设备等重点部位和重大危险源，并深入工地现场加大检查督查频次，发现隐患及时督促整改消除；措施落实到位。督促施工各企业及项目部，必须提前加固临时设施、撤离危险场所人员、备足配齐必要物资，及时清除外架平网、板房顶部及悬挑部位积雪，并在低温雨雪冰冻天气时段，禁止高空作业和一切设备安装、安拆和维保工作；应急值守到位。全面落实24小时值班和安全状况日报告制度，保持手机畅通，并通过微信、QQ安全监管群及时上情下达，下情上传，实时掌握工作动态，了解实情，使得工程建设各方主体责任得到有效落实。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">据了解，截至目前，全市103个在建工地的安全状况总体良好，没有出现任何险情。下一步，市建管处将重点管控好春节前可能继续施工作业的22个建筑工地，以确保节日期间的全市建筑施工安全生产形势持续平稳。</p><p><br/></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,0,0,'1','2018-02-05 20:44:15','2018-02-05 20:44:15','0'),('5cab3526-5098-4a92-bca5-60a6ce40687e','1','3bd70d72-c5eb-43af-af3e-5c2aa2d33f11','加强预警研判,做好重点车辆管控','','<p><span style=\"color: rgb(71, 71, 71); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; text-align: justify; background-color: rgb(255, 255, 255);\">为确保春运期间道路运输市场平稳、有序，江西省公路运输管理局和省公安厅交通管理局决定在今年春运期间联合开展打非治违专项整治活动，并成立全省打非治违专项整治活动领导小组。</span></p><p style=\"color: rgb(71, 71, 71); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>发布专项整治方案</strong></p><p style=\"color: rgb(71, 71, 71); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;据了解，各地通过开展打非治违专项整治活动，建立联合执法机制，维护道路运输市场秩序和道路运输安全，为全省春运工作顺利进行提供保障。</p><p style=\"color: rgb(71, 71, 71); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;此次重点整治区域，包括机场、汽车站、火车站、大中专院校、医院、集贸市场、旅游风景区、高速公路服务区、城乡接合部等客货集散地以及主要线路。</p><p style=\"color: rgb(71, 71, 71); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;重点查处无营运证或伪造营运证的客货运输车辆和其他从事非法营运的社会车辆；查处非法从事班线营运、起点均不在许可的经营区域的出租汽车经营；查处营运车辆超速超员、疲劳驾驶等行为，农村道路客车超员载客、货车违法载人、酒后驾驶、无证驾驶等突出违法行为；查处强行拉客、甩客、倒客、站外组客等违法经营行为；查处客运包车未持有效包车标志牌、不按包车标志牌载明事项运行、线路两端均不在车籍所在地、招揽包车合同以外的旅客乘车等违法经营行为；查处道路运输经营者不按规定安装或正常使用车载终端实时监控设备的违法行为；其他严重危害道路安全的行为。</p><p><br/></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,1,0,'1','2018-02-05 19:24:58','2018-02-05 19:24:58','0'),('5d8a9ec9-17ab-4ecb-971c-581d374d33f4','1','85627160-b3a8-423f-8648-e2bdccbbacae','乡村振兴战略谋划中国农村巨变蓝图','/media-data/image/20180205164829/1111.jpg','<p style=\"margin-top: 0px; margin-bottom: 15px; padding: 0px; color: rgb(64, 64, 64); font-family: &quot;PingFang SC&quot;, &quot;Lantinghei SC&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, å¾®è½¯é›…é»‘, STHeitiSC-Light, simsun, å®‹ä½“, &quot;WenQuanYi Zen Hei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; white-space: normal;\">实施乡村振兴战略，是党的十九大作出的重大决策部署，是决胜全面建成小康社会、全面建设社会主义现代化国家的重大历史任务，是新时代“三农”工作的总抓手。现就实施乡村振兴战略提出如下意见。</p><p style=\"margin-top: 0px; margin-bottom: 15px; padding: 0px; color: rgb(64, 64, 64); font-family: &quot;PingFang SC&quot;, &quot;Lantinghei SC&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, å¾®è½¯é›…é»‘, STHeitiSC-Light, simsun, å®‹ä½“, &quot;WenQuanYi Zen Hei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; white-space: normal;\"><span style=\"font-weight: 600 !important;\"><span style=\"color:#003366\">　　</span><span style=\"color:navy\">一、新时代实施乡村振兴战略的重大意义</span></span></p><p style=\"margin-top: 0px; margin-bottom: 15px; padding: 0px; color: rgb(64, 64, 64); font-family: &quot;PingFang SC&quot;, &quot;Lantinghei SC&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, å¾®è½¯é›…é»‘, STHeitiSC-Light, simsun, å®‹ä½“, &quot;WenQuanYi Zen Hei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; white-space: normal;\">　　党的十八大以来，在以习近平同志为核心的党中央坚强领导下，我们坚持把解决好“三农”问题作为全党工作重中之重，持续加大强农惠农富农政策力度，扎实推进农业现代化和新农村建设，全面深化农村改革，农业农村发展取得了历史性成就，为党和国家事业全面开创新局面提供了重要支撑。5年来，粮食生产能力跨上新台阶，农业供给侧结构性改革迈出新步伐，农民收入持续增长，农村民生全面改善，脱贫攻坚战取得决定性进展，农村生态文明建设显著加强，农民获得感显著提升，农村社会稳定和谐。农业农村发展取得的重大成就和“三农”工作积累的丰富经验，为实施乡村振兴战略奠定了良好基础。</p><p style=\"margin-top: 0px; margin-bottom: 15px; padding: 0px; color: rgb(64, 64, 64); font-family: &quot;PingFang SC&quot;, &quot;Lantinghei SC&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, å¾®è½¯é›…é»‘, STHeitiSC-Light, simsun, å®‹ä½“, &quot;WenQuanYi Zen Hei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; white-space: normal;\">　　农业农村农民问题是关系国计民生的根本性问题。没有农业农村的现代化，就没有国家的现代化。当前，我国发展不平衡不充分问题在乡村最为突出，主要表现在：农产品阶段性供过于求和供给不足并存，农业供给质量亟待提高；农民适应生产力发展和市场竞争的能力不足，新型职业农民队伍建设亟需加强；农村基础设施和民生领域欠账较多，农村环境和生态问题比较突出，乡村发展整体水平亟待提升；国家支农体系相对薄弱，农村金融改革任务繁重，城乡之间要素合理流动机制亟待健全；农村基层党建存在薄弱环节，乡村治理体系和治理能力亟待强化。实施乡村振兴战略，是解决人民日益增长的美好生活需要和不平衡不充分的发展之间矛盾的必然要求，是实现“两个一百年”奋斗目标的必然要求，是实现全体人民共同富裕的必然要求。</p><p style=\"margin-top: 0px; margin-bottom: 15px; padding: 0px; color: rgb(64, 64, 64); font-family: &quot;PingFang SC&quot;, &quot;Lantinghei SC&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, å¾®è½¯é›…é»‘, STHeitiSC-Light, simsun, å®‹ä½“, &quot;WenQuanYi Zen Hei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; white-space: normal;\">　　在中国特色社会主义新时代，乡村是一个可以大有作为的广阔天地，迎来了难得的发展机遇。我们有党的领导的政治优势，有社会主义的制度优势，有亿万农民的创造精神，有强大的经济实力支撑，有历史悠久的农耕文明，有旺盛的市场需求，完全有条件有能力实施乡村振兴战略。必须立足国情农情，顺势而为，切实增强责任感使命感紧迫感，举全党全国全社会之力，以更大的决心、更明确的目标、更有力的举措，推动农业全面升级、农村全面进步、农民全面发展，谱写新时代乡村全面振兴新篇章。</p><p><br/></p>',NULL,NULL,'0',0,0,0,'1','2018-02-05 16:48:32','2018-02-05 16:48:32','1'),('697b5712-22ef-4d2a-bd91-cbe578ec0ab6','1','2108dfa2-1e27-4b3d-87d7-7088d46983de','交通运输部关于发布公路工程标准施工招标文件及公路工程标准施工招标资格预审文件2018年版的公告','','<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; font-family: Arial, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);\">　为加强公路工程施工招标管理，规范招标文件及资格预审文件编制工作，依照《中华人民共和国招标投标法》《中华人民共和国招标投标法实施条例》等法律法规，按照《公路工程建设项目招标投标管理办法》（交通运输部令2015年第24号），在国家发展改革委牵头编制的《标准施工招标文件》及《标准施工招标资格预审文件》（以下简称《标准文件》）基础上，结合公路工程施工招标特点和管理需要，交通运输部组织制定了《公路工程标准施工招标文件》（2018年版）及《公路工程标准施工招标资格预审文件》（2018年版）（以下简称《公路工程标准文件》），现予发布。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; font-family: Arial, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);\">　　《公路工程标准文件》（2018年版）自2018年3月1日起施行，原《公路工程标准文件》（交公路发〔2009〕221号）同时废止，之前根据《公路工程标准文件》（2009年版）完成招标工作的项目仍按原合同执行。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; font-family: Arial, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);\">　　自施行之日起，依法必须进行招标的公路工程应当使用《公路工程标准文件》（2018年版），其他公路项目可参照执行。在具体项目招标过程中，招标人可根据项目实际情况，编制项目专用文件，与《公路工程标准文件》（2018年版）共同使用，但不得违反国家有关规定。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; font-family: Arial, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);\">　　《公路工程标准文件》（2018年版）中“申请人须知”“资格审查办法”“投标人须知”“评标办法”和“通用合同条款”等部分，与《标准文件》内容相同的只保留条目号，具体内容见《标准文件》。《公路工程标准文件》电子文本可在交通运输部网站(www.mot.gov.cn)“下载中心”下载。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; font-family: Arial, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);\">　　请各省级交通运输主管部门加强对《公路工程标准文件》（2018年版）贯彻落实情况的监督检查，注意收集有关意见和建议，及时反馈。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; font-family: Arial, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-align: right;\">　　交通运输部&nbsp;&nbsp;&nbsp;&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; font-family: Arial, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-align: right;\">　　2017年11月30日&nbsp;</p><p><br/></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,0,0,'1','2018-02-05 19:43:36','2018-02-05 19:43:36','0'),('793dc262-6292-415f-900b-529f3979d37f','1','6bea8a78-4c27-45c5-92f7-cef4679d2479','关于召开安徽扫黑除恶专项斗争电视电话会议的通知','','<p style=\"padding: 0px;margin-top: 0px;margin-bottom: 0px;margin-left: 0;font-family: &#39;Microsoft YaHei&#39;;font-size: 12px;text-indent: 0;white-space: normal;background: rgb(255, 255, 255);line-height: 18px\"><span style=\"padding: 0px;line-height: 24px;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\">&nbsp;</span><span style=\"padding: 0px;line-height: 24px;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\">1月27日，全省扫黑除恶专项斗争电视电话会议召开，对全省开展一轮新的扫黑除恶专项斗争进行动员部署，要求春节前要取得初步战果。省委书记李锦斌，省委副书记、省长李国英分别作出批示。省委常委、政法委书记姚玉舟出席会议并讲话，副省长、省公安厅长李建中主持会议,张坚、薛江武出席会议。</span></p><p style=\"padding: 0px;margin-top: 0px;margin-bottom: 0px;margin-left: 0;font-family: &#39;Microsoft YaHei&#39;;font-size: 12px;text-indent: 0;white-space: normal;background: rgb(255, 255, 255);line-height: 18px\"><span style=\"padding: 0px;line-height: 24px;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\">&nbsp;</span></p><p style=\"padding: 0px;margin-top: 0px;margin-bottom: 0px;margin-left: 0;font-family: &#39;Microsoft YaHei&#39;;font-size: 12px;text-indent: 0;white-space: normal;background: rgb(255, 255, 255);line-height: 18px\"><span style=\"padding: 0px;line-height: 24px;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">　　李锦斌指出，开展扫黑除恶专项斗争，这是以习近平同志为核心的党中央立足国家长治久安和社会大局和谐稳定作出的一项重大决策部署。全省各级党委政府要深入学习贯彻习近平总书记重要指示和中央《通知》精神，按照全国扫黑除恶专项斗争电视电话会议部署要求，提高政治站位，增强</span>“四个意识”，以决胜的精神状态和雷霆的实际行动，坚决打赢扫黑除恶专项斗争这场硬战。</span></p><p><br/></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,0,0,'1','2018-02-04 11:12:23','2018-02-04 11:12:23','0'),('7b7b9ca0-0701-4708-840c-7a9e9b063772','1','67982d29-473e-41cd-bf60-c67488e3b0f6','国务院办公厅关于2018年 部分节假日安排的通知','','<p>						</p><p><br/></p><p>国务院办公厅关于2018年</p><p>部分节假日安排的通知</p><p>国办发明电〔2017〕12号</p><p><br/></p><p>各省、自治区、直辖市人民政府，国务院各部委、各直属机构：</p><p>经国务院批准，现将2018年元旦、春节、清明节、劳动节、端午节、中秋节和国庆节放假调休日期的具体安排通知如下。</p><p>一、元旦：1月1日放假，与周末连休。</p><p>二、春节：2月15日至21日放假调休，共7天。2月11日（星期日）、2月24日（星期六）上班。</p><p>三、清明节：4月5日至7日放假调休，共3天。4月8日（星期日）上班。</p><p>四、劳动节：4月29日至5月1日放假调休，共3天。4月28日（星期六）上班。</p><p>五、端午节：6月18日放假，与周末连休。</p><p>六、中秋节：9月24日放假，与周末连休。</p><p>七、国庆节：10月1日至7日放假调休，共7天。9月29日（星期六）、9月30日（星期日）上班。</p><p>节假日期间，各地区、各部门要妥善安排好值班和安全、保卫等工作，遇有重大突发事件，要按规定及时报告并妥善处置，确保人民群众祥和平安度过节日假期。</p><p style=\"text-align: right;\">国务院办公厅 &nbsp; &nbsp; &nbsp;&nbsp;</p><p style=\"text-align: right;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; text-align: right; background-color: rgb(255, 255, 255);\">2017年11月30日&nbsp;</span></p><p>\r\n					</p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,0,0,'1','2018-02-04 09:12:54','2018-02-04 09:13:18','0'),('7b81f1d3-cae2-45ba-a1cf-f3b8dfe81d32','3','9f244aef-f2b6-450b-af7f-a966ef71e357','110宣传日，守护新时代美好生活（测试）',NULL,'<p style=\"text-align: center;\"><video class=\"edui-upload-video  vjs-default-skin video-js\" controls=\"\" preload=\"none\" width=\"420\" height=\"280\" src=\"/media-data/ueditor/video/20180205/1517836852271070725.mp4\" data-setup=\"{}\"></video></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,1,0,'1','2018-02-05 21:20:57','2018-02-05 21:20:57','0'),('7e7296e2-ee63-472a-83c4-a225dc02a642','2','e89a7d96-d7b5-4840-8e0d-bbdcbb6d4b98','公安局交警支队送温暖活动',NULL,'<p style=\"text-align: center;\"><img src=\"/media-data/ueditor/image/20180205/1517793322772041295.jpg\" title=\"1517793322772041295.jpg\" alt=\"jj.jpg\"/></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,0,0,'1','2018-02-05 09:15:30','2018-02-05 09:15:30','0'),('8024aa7d-c123-4cb6-84e0-68eec0e75b91','1','7ef804ac-a238-4277-a4e0-dd1e155c3751','发放返乡车费补助助力外省民工过年','','<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">2月1日，从当涂经开区负责人手中接过200元返乡车费补助，盛达前亮铝业公司工人杨炳增非常惊喜：“回家的火车票已买好了，从马鞍山到广州，单程201元，没想到经开区给报销了。”</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">据了解，当涂经开区入驻企业已超过800家，吸引了英国利洁时、意大利百乐得、中国大唐、顺丰控股、奥克斯集团等一批知名企业落户。目前，省外农民工约1500人。临近春节，为增强外地农民工在当涂经开区工作、生活的幸福感和归属感，当涂经开区开展了“农民工平安返乡”暨困难职工“送温暖”活动，本次发放农民工平安返乡车费补助约30万元。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: right; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">（记者吴黎明通讯员王永刚陆菲）</p><p><br/></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,0,0,'1','2018-02-05 20:16:01','2018-02-05 20:16:01','0'),('81b59c40-17f8-4e97-bd1f-20bb044b47d7','1','85627160-b3a8-423f-8648-e2bdccbbacae','新华网评：让农村成为安居乐业的美丽家园','/media-data/image/20180205170611/zg.jpg','<p style=\"margin-top: 0px; margin-bottom: 15px; padding: 0px; color: rgb(64, 64, 64); font-family: &quot;PingFang SC&quot;, &quot;Lantinghei SC&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, å¾®è½¯é›…é»‘, STHeitiSC-Light, simsun, å®‹ä½“, &quot;WenQuanYi Zen Hei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; white-space: normal;\">作为改革开放以来第20个、新世纪以来第15个中央一号文件，4日发布的《中共中央 国务院关于实施乡村振兴战略的意见》提出，走中国特色社会主义乡村振兴道路，让农业成为有奔头的产业，让农民成为有吸引力的职业，让农村成为安居乐业的美丽家园。</p><p style=\"margin-top: 0px; margin-bottom: 15px; padding: 0px; color: rgb(64, 64, 64); font-family: &quot;PingFang SC&quot;, &quot;Lantinghei SC&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, å¾®è½¯é›…é»‘, STHeitiSC-Light, simsun, å®‹ä½“, &quot;WenQuanYi Zen Hei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; white-space: normal;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 15px; padding: 0px; color: rgb(64, 64, 64); font-family: &quot;PingFang SC&quot;, &quot;Lantinghei SC&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, å¾®è½¯é›…é»‘, STHeitiSC-Light, simsun, å®‹ä½“, &quot;WenQuanYi Zen Hei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; white-space: normal;\">　　不忘初心，方得始终。今年是改革开放40周年。40年前，安徽省滁州市凤阳县小岗村18户村民按下红手印，一份大包干的“生死契约”，成为中国改革开放的一声春雷。改革开放以来农村改革的伟大实践，推动我国农业生产、农民生活、农村面貌发生了巨大变化。特别是党的十八大以来，中国粮食生产能力跨上新台阶，农业供给侧结构性改革迈出新步伐，农民收入持续增长，农村民生全面改善，6600多万贫困人口稳定脱贫，农业农村发展取得历史性成就、发生历史性变革。</p><p style=\"margin-top: 0px; margin-bottom: 15px; padding: 0px; color: rgb(64, 64, 64); font-family: &quot;PingFang SC&quot;, &quot;Lantinghei SC&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, å¾®è½¯é›…é»‘, STHeitiSC-Light, simsun, å®‹ä½“, &quot;WenQuanYi Zen Hei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; white-space: normal;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 15px; padding: 0px; color: rgb(64, 64, 64); font-family: &quot;PingFang SC&quot;, &quot;Lantinghei SC&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, å¾®è½¯é›…é»‘, STHeitiSC-Light, simsun, å®‹ä½“, &quot;WenQuanYi Zen Hei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; white-space: normal;\">　　成就彪炳史册，征程任重道远。在肯定成绩的同时，也要正视问题。比如，我国发展不平衡不充分的问题在农村最为突出，农产品阶段性供过于求和供给不足并存，农村基础设施和民生领域欠账较多，农村环境和生态问题比较突出，农村基层党建存在薄弱环节。实施乡村振兴战略，是解决人民日益增长的美好生活需要和不平衡不充分的发展之间矛盾的必然要求，是实现“两个一百年”奋斗目标的必然要求，是实现全体人民共同富裕的必然要求。</p><p style=\"margin-top: 0px; margin-bottom: 15px; padding: 0px; color: rgb(64, 64, 64); font-family: &quot;PingFang SC&quot;, &quot;Lantinghei SC&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, å¾®è½¯é›…é»‘, STHeitiSC-Light, simsun, å®‹ä½“, &quot;WenQuanYi Zen Hei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; white-space: normal;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 15px; padding: 0px; color: rgb(64, 64, 64); font-family: &quot;PingFang SC&quot;, &quot;Lantinghei SC&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, å¾®è½¯é›…é»‘, STHeitiSC-Light, simsun, å®‹ä½“, &quot;WenQuanYi Zen Hei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; white-space: normal;\">　　提纲挈领，纲举目张。实现乡村振兴，让农村成为安居乐业的美丽家园，关键在党，要坚持和完善党对“三农”工作的领导，真正把实施乡村振兴战略摆在优先位置，把党管农村工作的要求落到实处。正如一号文件强调，把实现乡村振兴作为全党的共同意志、共同行动，做到认识统一、步调一致，在干部配备上优先考虑，在要素配置上优先满足，在资金投入上优先保障，在公共服务上优先安排，加快补齐农业农村短板。</p><p style=\"margin-top: 0px; margin-bottom: 15px; padding: 0px; color: rgb(64, 64, 64); font-family: &quot;PingFang SC&quot;, &quot;Lantinghei SC&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, å¾®è½¯é›…é»‘, STHeitiSC-Light, simsun, å®‹ä½“, &quot;WenQuanYi Zen Hei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; white-space: normal;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 15px; padding: 0px; color: rgb(64, 64, 64); font-family: &quot;PingFang SC&quot;, &quot;Lantinghei SC&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, å¾®è½¯é›…é»‘, STHeitiSC-Light, simsun, å®‹ä½“, &quot;WenQuanYi Zen Hei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; white-space: normal;\">　　让农村成为安居乐业的美丽家园，要坚持以人民为中心的思想，充分尊重农民意愿，切实发挥农民在乡村振兴中的主体作用。比如，在推进与农民切身利益相关的各项改革措施时，应该发扬农民首创精神，切实保障农民合法权益。政府以及城市工商资本“下乡”，要带动而不是代替农民决策、抉择。要把握让农业成为有奔头的产业、让农民成为有吸引力的职业与让农村成为安居乐业的美丽家园的内在关系，使农业现代化、农民专业化、农村美丽化相互促进、相互带动、相互融合，从而使乡村振兴与城市化发展同步，使城乡二元化格局在城乡融合中获得历史性破题。</p><p><br/></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,0,0,'1','2018-02-05 17:06:15','2018-02-05 17:06:15','0'),('8c8b0b6d-7455-4925-a95f-ddc3cb3101d7','1','5a04f14f-4d6f-435a-a6ac-393a2f6e8d11','官员的朋友圈应该是啥样的？','','<p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">学无前后　达者为师</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　我当兵时是一个团里的打字员。一次，政委和宣传干事在打字室讨论一个《情况汇报》材料。政委说，汇报中用“听到对方的叫骂声”太生硬，能否换一个词代替“叫骂声”？宣传干事说，部队赶到事发现场维护秩序时，就是听到当事人还在骂村干部。我也想换一个词，但一时又想不出一个更适合的词来替代。政委看了我一眼后说，杨进峰，你能想出一个更适合的词吗？</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　宣传干事是团部里有名的笔杆子，政委的文才更是众人皆知。政委问我，我受宠若惊，但又不敢班门弄斧。政委就好像看穿了我的心思，笑了笑说，学无前后，达者为师，你说一个看看，说得好就用你说的。我随口说，“听到对方的训斥声”，如何？政委听后连声说妙，最后真用了“训斥”代替“叫骂”。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　后来，在一次全团大会上，政委讲了我们这个替换词语的故事，而且对所有干部说：“我们每个干部，心中都要装着战士，要能听得进战士的心声。战士的学问不一定处处都比干部差，只要战士说出的观点正确，干部就应该采纳。”</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　走出部队这么多年，政委的话一直记在我心里。我想，任何一名正直、通达的官员，不论级别地位如何，他对身边人一定是一视同仁的；他的朋友圈也一定是不分高低贵贱的——能够听得到各级各类人员的声音，能够听取任何有益有助的建议。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　学无前后，达者为师，古之圣人，其出人也远矣，犹且从师而问焉，更何况我等普通人呢？</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　（杨进峰　银川日报社）</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　第二“练兵场”</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　“最美好的生活方式，是和一群志同道合的人，一起奔跑在理想的路上……”一早起床，看到检察长发的微信朋友圈，顿觉干劲十足。转载最新的法律条文，分享励志小故事，传播社会正能量……他的朋友圈俨然成了激发干警工作热情、提高干警业务能力的第二“练兵场”。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　这样的一股清流，与当下不少官员在朋友圈总保持沉默的现象形成了鲜明的对比。不管是因为思想上的谨慎，还是因为心理上的压力，这种低调旁观的方式都不值得我们提倡。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　当下是新媒体和自媒体蓬勃发展的时代，作为官员，要充分利用现代传媒新手段新方式，创造性地为发展找到新思路新方向。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　要转变思维，敢于发声——作为社会发展的中坚力量，官员一方面应该主动借助新媒体的传播优势，了解最真实的社情民意；另一方面，也应该真实表露自己的观点，引导正确的舆论。这样才能制定有效的措施确保惠民政策落实，推动实际工作开展，取得事倍功半的效果。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　要顶住压力，善于发声——近年来，镇长网上直播，推销当地农产品；基层干部拍写真，为家乡旅游业代言……这些做法尽管评价声褒贬不一，但都取得了实际效果。作为官员，应该学习这种迎难而上的精神，善于利用手中的权力，为人民群众办实事。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　（丁玫月　山东省胶州市检察院）</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　从“不赴无名之宴”开始</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　人在社会中生活，难免人情世故。对于公职人员，特别是官员来说，由于身份的特殊性，在处理人际关系方面当有明确的原则。具体怎么做？且从不赴无名之宴开始吧。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　一个周末，我去参加一个同学聚会。饭吃了一半才弄明白，有人是想通过组织这场聚会，结识我的一位当局长的同学。于是，我找了个借口早早退席，怕无形之中充当了不恰当的角色。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　从此，再有人叫吃饭，我一定会问清由头和参加的人员，对于不明不白的饭局一律不参加。我的做法也得到了一些官员朋友的认可和效仿。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　因为工作关系，我参与查办过多起职务犯罪案件。一些涉案官员，本该发挥应有的作用，为民服务，勤勉工作，就因为没能管住自己的嘴、手和腿，更没有管理好自己的朋友圈，结果身陷囹圄。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　习近平总书记在《生活情趣非小事》的文章中，对领导干部提出要求：“慎重对待朋友交往，明辨是非，克己慎行，讲操守，重品行，时刻检点自己生活的方方面面”。唐太宗李世民在《百字箴言》中也有一句话说得很好：“交有德之朋，绝无义之友。取本分之财，戒无名之酒。”我想，这些都是对官员在经营朋友圈方面极好的忠告。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　（杜永清　陕西省宝鸡市陈仓区检察院）</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　我来说两句：</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　官员的朋友圈该是什么样？他该是朋友圈里默默放大女儿照片看清楚的父亲；是给隔壁母亲集优惠随手点赞的邻居；是转发时事热点和人生哲理的思考者……老百姓的朋友圈啥样，官员的朋友圈就应该啥样。如果说有什么不应该的，那就是不应该对谣言的传播熟视无睹，不应该容忍把无知当乐趣、把庸俗当流行。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　——黄泽龙　广州市天河区检察院</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　官员应注意净化朋友圈，以组织部考察干部的标准慎重交友，择善而交，择廉行事，抵住诱惑，耐住寂寞，慎言慎行。</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　——葛东升　江苏省泰州市检察院</p><p style=\"margin: 10px auto; padding: 0px; font-stretch: normal; font-size: 18px; line-height: 36px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">　　领导干部，在某种程度上是一种风向标，在交朋友的时候更应该注意。交良友，则履职逾尽力、本领日益增进；交损友，则日益衰退。近年来，因交友不慎而落马的官员不少，此乃“前车之鉴”，当深刻反省在日常的交友过程中，是否逾越了底线。</p><p><br/></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,1,0,'1','2018-02-05 19:50:34','2018-02-05 19:50:34','0'),('8d76a226-5b92-4ac0-a0a8-8f0d6b4d9148','1','d1e63ab5-87f6-4043-8cfb-6ffc224bf373','抓基层党建述职评议会议纪要','','<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">2月1日，市委召开县区委书记抓基层党建述职评议会。市委书记张岳峰主持会议并讲话。他强调，要认真落实中央和省委、市委的部署要求，一心一意谋发展，聚精会神抓党建，推动全面从严治党向基层延伸，进一步提高基层党建工作质量，为全力推动“三个走在前列”提供坚强保证。市领导从辉、李群、孙勇、苏从勇、吴桂林、范奇等参加会议。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">会上，各县区委书记，市开发区、慈湖高新区、郑蒲港新区、示范园区党工委书记，市委非公工委、市直机关工委、市教育局党委书记分别就履行抓基层党建职责情况作了述职发言。张岳峰认真听取大家的发言，并代表市委逐一进行点评。述职人员作了表态发言，与会同志现场对述职人员进行了评议。省委组织部相关部门负责同志到会指导。</p><p style=\"text-align: center;\"><img src=\"/media-data/ueditor/image/20180205/1517835625994037926.jpg\" title=\"1517835625994037926.jpg\" alt=\"hy.jpg\"/></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,0,0,'1','2018-02-05 21:00:32','2018-02-05 21:00:32','0'),('8e60c014-1f14-4038-b17b-bcea54d7a1de','1','7ef804ac-a238-4277-a4e0-dd1e155c3751','马鞍山去年公积金发放贷款16亿余元','','<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">2月2日上午，马鞍山住房公积金管理中心举行新闻发布会，现场公布了全市2017年住房公积金大数据。2017年，全市共向6316户家庭发放公积金贷款16.39亿元，为“深化住房制度改革，加快建立多主体供给和多渠道保障”提供了强有力的资金保障。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"margin: 0px; padding: 0px; list-style: none;\">全年发放贷款16.39亿元支持购房69.24万平方米</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">2017年，全市共向6316户家庭发放公积金贷款16.39亿元，完成全年计划的<span style=\"margin: 0px; padding: 0px; list-style: none; text-indent: 2em;\">95.86%。</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">其中：市本级向3501户家庭发放公积金贷款9.32亿元，马钢分中心向1134户家庭发放公积金贷款2.86亿元，当涂管理部向753户家庭发放公积金贷款1.76亿元，和县管理部向662户家庭发放公积金贷款1.76亿元，含山管理部向266户家庭发放公积金贷款0.69亿元。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">2017年，全市公积金贷款共支持个人商品住房消费面积69.24万平方米。其中：个人一手住房面积为44.54万平方米，二手住房面积为24.7万平方米。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">截至2017年底，全市累计发放公积金贷款88606笔，共159.55亿元，目前贷款余额为85.44亿元。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">据市住房公积金管理中心主任李超先介绍，2017年，全市住房公积金支取保持上升趋势，全年支取住房公积金26.92亿元，占全年计划安排的118.58%，同比增长3.37%。截至2017年底，全市累计支取住房公积金193.32亿元。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">“2017年，全市年末住房公积金资金综合存贷比为95.71%，较年初下降2.17个百分点。”李超先表示，此项指标证明了全市的住房公积金使用是充分的。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"margin: 0px; padding: 0px; list-style: none;\">全年归集公积金32.13亿元新增开户职工20824名</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">数据显示，2017年，全市共归集住房公积金32.13亿元，完成全年计划的114.74%，同比增长0.56%。其中：市本级归集公积金15.34亿元，马钢分中心归集公积金8.29亿元，当涂管理部归集公积金2.68亿元，和县管理部归集公积金3.13亿元，含山管理部归集公积金2.69亿元。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">“截至2017年底，全市累计归集公积金282.59亿元，总量位居全省前列。目前，全市公积金缴存余额为89.27亿元。”李超先表示，这个数据表明全市的住房公积金归集仍保持非常平稳的态势。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">2017年，全市共新增开户单位231个，新增开户职工20824名。其中：市本级新增开户单位137个，新开户职工9478名；马钢分中心新增开户单位7个，新开户职工1072名；当涂管理部新增开户单位32个，新开户职工6727名；和县管理部新增开户单位29个，新开户职工1988名；含山管理部新增开户单位26个，新开户职工1559名。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">李超先介绍说，去年全市公积金归集扩面工作卓有成效，让更多人群享受到了住房公积金惠民政策。2017年，全市有220名个人申请缴存了住房公积金，有40名进城务工农民开户缴存了住房公积金。截至2017年底，全市共有761人以个人申请开户缴存住房公积金。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">“您还没有走出办事大厅，钱就打到您的卡上了。”</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"margin: 0px; padding: 0px; list-style: none;\">新一代信息系统上线打通服务“最后一公里”</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">2017年，市住房公积金管理中心以“互联网+公积金”建设模式，高标准建设的新一代公积金信息系统于11月份上线运行，实现了办理流程“简化”、办结速度“量化”和服务质量“优化”，打通了住房公积金便民服务的“最后一公里”。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">“您还没有走出办事大厅，钱就打到您的卡上了。”李超先介绍说，新一代信息系统通过与住建部全国住房公积金结算应用系统对接，实现了职工提取住房公积金秒级到账。也就是说，职工在办</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">理公积金提取时，不需填写申请表，不需单位盖章，也不需提供复印件，只要符合提取条件和资料齐全，提供本人持有的银行一类借记卡，就可实时到账。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">同时，自2017年12月18日开始，原公积金贷款职工已申请了“按月划转还贷”的，将由系统统一自动调整为“按月冲还贷”。即住房公积金贷款按月还贷的，不再按上月已还贷款本息转入绑定的银行还款账户，而是用贷款职工及其配偶的住房公积金账户余额直接冲抵当月的应还贷款本息，余额不足部分再从其申请贷款时绑定的银行还款账户中扣划。此项还款方式的改变，不仅减少了职工账户资金流动而造成的利息损失，而且对提前还款职工更方便，无需先垫付大额资金，然后再来办理公积金提取了。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">新系统上线后，除在马钢分中心缴存的全市住房公积金缴存职工，在全市任何一个住房公积金网点都可申请办理住房公积金提取或转移，不再受住房公积金缴存地限制。同时，全国各地来马鞍山工作的职工，都可将在原工作地缴存的公积金转移到马鞍山，不再需要去原工作地住房公积金中心办理相关手续，减少了群众来回奔波，真正实现了“账随人走、钱随账走”。并将实现网上汇缴，系统自动入账，网上提取，不见面审批，以及网上申请、网上受理和网上预约等。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: right; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">本报记者谈恒成&nbsp;通讯员&nbsp;万霞</p><p><br/></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,0,0,'1','2018-02-05 20:11:36','2018-02-05 20:11:36','0'),('a528a50e-f34e-4cd2-b480-ccff0fc37a04','1','88505d9c-e286-486f-97ec-a34cc2248423','马鞍山市公安局交警支队2018年公开招聘警务辅助人员体检公告','','<p>						</p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 5px 0px;text-indent: 43px;line-height: 1.8em;color: rgb(1, 1, 1);font-family: Arial, Helvetica, sans-serif;font-size: 13px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\";padding: 0px;font-family: 仿宋_GB2312;font-size: 21px\"><span style=\"font-family:仿宋_GB2312\">根据《</span>2018年马鞍山市公安局交警支队公开招聘警务辅助人员招聘方案》要求，按“笔试成绩×</span><span style=\";padding: 0px;font-family: 仿宋_GB2312;font-size: 21px\">0.3+</span><span style=\";padding: 0px;font-family: 仿宋_GB2312;font-size: 21px\"><span style=\"font-family:仿宋_GB2312\">体能测试成绩</span>×</span><span style=\";padding: 0px;font-family: 仿宋_GB2312;font-size: 21px\">0.3+</span><span style=\";padding: 0px;font-family: 仿宋_GB2312;font-size: 21px\"><span style=\"font-family:仿宋_GB2312\">面试成绩</span>×0.4+加分”的公式计算总成绩，按照公布岗位招聘计划数1:1比例，从高分到低分依次等额确定参加体检人员名单。现将面试人员成绩及通过面试进入体检人员相关事宜通知如下：</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;margin-left: 55px;padding: 5px 0px;line-height: 1.8em;color: rgb(1, 1, 1);font-family: Arial, Helvetica, sans-serif;font-size: 13px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\";padding: 0px;font-family: 仿宋_GB2312;font-size: 21px\">一、&nbsp;</span><span style=\";padding: 0px;font-family: 仿宋_GB2312;font-size: 21px\">面试成绩、总成绩</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;margin-left: 55px;padding: 5px 0px;line-height: 1.8em;color: rgb(1, 1, 1);font-family: Arial, Helvetica, sans-serif;font-size: 13px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\";padding: 0px;font-family: 仿宋_GB2312;font-size: 21px\">二、&nbsp;</span><span style=\";padding: 0px;font-family: 仿宋_GB2312;font-size: 21px\">入围体检人员名单</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;margin-left: 55px;padding: 5px 0px;line-height: 1.8em;color: rgb(1, 1, 1);font-family: Arial, Helvetica, sans-serif;font-size: 13px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\";padding: 0px;font-family: 仿宋_GB2312;font-size: 21px\">三、&nbsp;</span><span style=\";padding: 0px;font-family: 仿宋_GB2312;font-size: 21px\">体检事项</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 5px 0px;text-indent: 43px;line-height: 1.8em;color: rgb(1, 1, 1);font-family: Arial, Helvetica, sans-serif;font-size: 13px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\";padding: 0px;font-family: 仿宋_GB2312;font-size: 21px\"><span style=\"font-family:仿宋_GB2312\">请入围面试人员携本人身份证和准考证，于</span>2018年1月1</span><span style=\";padding: 0px;font-family: 仿宋_GB2312;font-size: 21px\">8</span><span style=\";padding: 0px;font-family: 仿宋_GB2312;font-size: 21px\"><span style=\"font-family:仿宋_GB2312\">日和</span>1</span><span style=\";padding: 0px;font-family: 仿宋_GB2312;font-size: 21px\">9</span><span style=\";padding: 0px;font-family: 仿宋_GB2312;font-size: 21px\">日到体检中心进行体检，体检当天空腹前往。逾期未参加者视为放弃。</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 5px 0px;text-indent: 2em;line-height: 1.8em;color: rgb(1, 1, 1);font-family: Arial, Helvetica, sans-serif;font-size: 13px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\";padding: 0px;font-family: 仿宋_GB2312;font-size: 21px\">&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 5px 0px;text-indent: 43px;line-height: 1.8em;color: rgb(1, 1, 1);font-family: Arial, Helvetica, sans-serif;font-size: 13px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\";padding: 0px;font-family: 仿宋_GB2312;font-size: 21px\">&nbsp;</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 5px 0px;text-indent: 2em;line-height: 1.8em;color: rgb(1, 1, 1);font-family: Arial, Helvetica, sans-serif;font-size: 13px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\";padding: 0px;font-family: 仿宋_GB2312;font-size: 21px\">&nbsp;</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 5px 0px;text-indent: 43px;line-height: 1.8em;color: rgb(1, 1, 1);font-family: Arial, Helvetica, sans-serif;font-size: 13px;white-space: normal;background-color: rgb(255, 255, 255);text-align: right\"><span style=\";padding: 0px;font-family: 仿宋_GB2312;font-size: 21px\"><span style=\"color: rgb(1, 1, 1); font-family: 仿宋_GB2312; font-size: 21px; text-indent: 43px; background-color: rgb(255, 255, 255);\">马鞍山市</span>公安局交警支队</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 5px 0px;text-indent: 43px;line-height: 1.8em;color: rgb(1, 1, 1);font-family: Arial, Helvetica, sans-serif;font-size: 13px;white-space: normal;background-color: rgb(255, 255, 255);text-align: right\"><span style=\";padding: 0px;font-family: 仿宋_GB2312;font-size: 21px\">2018年1月1</span><span style=\";padding: 0px;font-family: 仿宋_GB2312;font-size: 21px\">7</span><span style=\";padding: 0px;font-family: 仿宋_GB2312;font-size: 21px\">日</span></p><p><br/></p><p>\r\n					</p>',NULL,NULL,'0',0,0,0,'1','2018-02-04 10:11:50','2018-02-04 14:21:30','1'),('ac55b276-bfa9-4bf6-89c9-b00d879b556a','1','0ccc3466-2f38-4640-ad22-92fced9f45ba','民警手记','','<p><br/></p><p>&nbsp; &nbsp;今天又是一个风和日丽的好日子，恰逢周未双休。上午8点左右，我陪妻子一起上街买菜，准备带回老家看望年迈的父母。我们买了饺子皮和金银花，又买了刚上市的黄瓜，在一板车前正准备买芒果，有位双手还护着挎包的中年女子，神色慌张地对我们说：“刚才我的挎包被一个怀孕妇女打开了，如果不是这位卖芒果的老板及时提醒我，今天我肯定会失财了”。中年女子买好芒果就消失在人群之中。那位卖芒果老板悄悄地告诉我说：“确实有三位孕妇，她们还带着一个五六岁大的小孩，其中一位孕妇趁那位中年女子买芒果时，打开了她的挎包拉链，我站在高处看得很清楚”。</p><p>&nbsp; &nbsp; 我马上意识到这不是一次简单的行窃行为，很有可能是一起团伙作案。最近刑侦的同事们也在此处发现多起扒窃案件，已蹲守好久了，一直没有结果。我不禁心里一动，今日有妻子买菜作掩护，何不也便衣守一回，说不定有收获。这时卖芒果的老板又说：“我们做小买卖生意的都不敢多事，刚才那位买芒果的妇女如不是我的暗示，肯定会让小偷得逞了”。我听毕，凭着警察的直觉，料定那几位小偷因下手没有“成功”，不会轻易离开。于是，我立即朝密集人群中搜寻起来，当我的眼光扫视到离我们不到6米远的对面蔬菜推位时，发现有几位妇女围着一位衣着朴素年近60多岁的老人，其中有一位孕妇左手用衣服做掩护，右手去扒那位老人挂在腰间的钱包，大概是想看一下钱包内有没有钱。我在对面看着十分清楚，老人的钱包被那位妇女扒开后，露出好几张百元大钞。就在那名孕妇把手伸入钱包内抽出百元大钞时，我飞步向前，大喊一声“快抓小偷”，一手扭住那妇女的手。那妇女顿时惊惶失措，开始一愣，接着大叫着“我没有偷钱，我真的没有偷钱”，还不断地央求我放了她，并拼命挣扎，又好像暗示另外的同伙快点离开。我锁紧眉头，怒张双眼，虎视那妇女道“我是便衣警察，人赃俱在，还想抵赖。”此时那位被偷的老人才恍然醒悟过来，马上查看自己的钱包，发现自已的钱包已经被打开，原来整齐放在里面的百元大钞被拉出一半，他又惊又喜地说：“幸亏这位同志的发现，如果钱被偷了，我真的不知如何是好，谢谢你，谢谢你！”</p><p>&nbsp; &nbsp; 此时围观的人也越来越多，大家都想要亲眼看一看这贼的模样，有的说是便衣警察抓住小偷了，还有的说该把小偷拉出去毒打一顿，给她一个教训。我看围观的人群不断增多，生怕孕妇发生意外，把那孕妇拉到小巷内，拔打了110。过不多久，警笛从远处而来，我向处警民警简单介绍案件后，把那妇女移交给了民警。此时的我心情才平静下来，围观的人群也慢慢地散去，菜场又恢复了正常秩序。</p><p>&nbsp; &nbsp; 后来经过了解，那位扒窃的怀孕妇女是四川籍人，并且还有犯罪的前科。抓获后一直不肯说出真实身份，后来我们的刑侦部门通过新近引进的高科技——人脸识别比对系统，将她的真实身份查了出来。被偷的老人是本地周山人，平时很少带钱，因他近期身体不好，那天身上带有2500多元钱，想买菜做佛事，并想到医院看一下病，这钱对他来说可是救命钱呀。</p><p>&nbsp; &nbsp; 我想这个周末我做了一件有意义的事，打击犯罪不分休闲与否，这就是警察，这就是这个职业所赋予的特殊义务。我当牢记！</p><p><br/></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,0,0,'1','2018-02-05 19:38:14','2018-02-05 19:38:14','0'),('ad66c8d7-ce7f-46b5-b244-5f265ad4c936','1','0baa5f76-e9e5-40a0-8b76-0223681fe1d0','交警六大队广泛开展“安全生产月”交通安全咨询日活动','','<p style=\"transition: color 0.3s linear, background-color 0.3s linear, border-color 0.3s linear, transform 0.3s linear; margin-top: 0.5em; margin-bottom: 0.5em; font-family: simsun; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">活动中，大队民警根据实际情况，利用宣传栏、横幅等显而易见的形式向广大群众广泛宣传安全出行常识，倡议群众们要拒绝交通陋习，文明驾驶。同时，宣传民警向人民群众讲解了交通安全法律、法规和行车、乘车遇到紧急情况如何处置等知识，并向群众发放交通安全知识等宣传资料，提醒广大群众要提高安全出行意识。另外，民警还展示了交通警察日常执法装备，现场演示酒精测试仪使用方法，积极与现场群众进行互动体验，使广大群众进一步了解酒驾交通违法行为的危害，从而拒绝酒后驾驶。</p><p style=\"transition: color 0.3s linear, background-color 0.3s linear, border-color 0.3s linear, transform 0.3s linear; margin-top: 0.5em; margin-bottom: 0.5em; font-family: simsun; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　宣传期间，大队民警和协警耐心接受群众咨询，解答人民群众提出的各类交通问题和交通安全知识，让群众自觉摒弃交通陋习，营造出文明出行、安全出行的浓厚氛围。此次“安全宣传咨询日”活动，大队共向群众发放宣传材料300余份、摆放展板5块、受教育群众达500余人。通过此次活动，有效拓展了交通安全宣传的社会影响面，既将知识送到了群众中，提高群众的安全意识，也进一步密切了警民关系。</p><p><br/></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,0,0,'1','2018-02-04 14:15:19','2018-02-05 09:12:20','0'),('ada7edf8-97b0-4b7a-935a-a52af723dbeb','1','9a93fa99-2f13-42e6-97f3-9f04b659c5bd','安徽省公安厅关于印发《安徽省居民户口登记管理实施规定》的通知','','<p style=\"box-sizing: border-box; font-family: 宋体; text-indent: 0cm; white-space: normal; background-color: rgb(255, 255, 255); text-align: justify; line-height: 27pt; margin: 0cm 0cm 0pt; font-size: 16pt;\"><span style=\"box-sizing: border-box; font-family: 仿宋_GB2312;\">各设区市公安局：</span></p><p style=\"box-sizing: border-box; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: justify; line-height: 27pt; text-indent: 31.6pt; margin: 0cm 0cm 0pt; font-size: 16pt;\"><span style=\"box-sizing: border-box; font-family: 仿宋_GB2312;\">为贯彻落实《<span style=\"font-family: 仿宋_GB2312; font-size: 21.3333px; text-align: right; text-indent: 358px; background-color: rgb(255, 255, 255);\">安徽省</span>居民户口登记管理办法》（省政府第</span><span style=\"box-sizing: border-box;\">179</span><span style=\"box-sizing: border-box; font-family: 仿宋_GB2312;\">号令），方便群众办理户口登记事项，省厅制定了《<span style=\"font-family: 仿宋_GB2312; font-size: 21.3333px; text-align: right; text-indent: 358px; background-color: rgb(255, 255, 255);\">安徽省</span>居民户口登记管理实施规定》，现印发给你们，请严格遵照执行。</span><span style=\"box-sizing: border-box;\">2014</span><span style=\"box-sizing: border-box; font-family: 仿宋_GB2312;\">年</span><span style=\"box-sizing: border-box;\">8</span><span style=\"box-sizing: border-box; font-family: 仿宋_GB2312;\">月</span><span style=\"box-sizing: border-box;\">25</span><span style=\"box-sizing: border-box; font-family: 仿宋_GB2312;\">日省厅公布的《<span style=\"font-family: 仿宋_GB2312; font-size: 21.3333px; text-align: right; text-indent: 358px; background-color: rgb(255, 255, 255);\">安徽省</span>公安机关户口管理规范》（皖公综〔</span><span style=\"box-sizing: border-box;\">2014</span><span style=\"box-sizing: border-box; font-family: 仿宋_GB2312;\">〕</span><span style=\"box-sizing: border-box;\">447</span><span style=\"box-sizing: border-box; font-family: 仿宋_GB2312;\">号）同时废止。</span></p><p style=\"box-sizing: border-box; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: justify; line-height: 27pt; text-indent: 31.6pt; margin: 0cm 0cm 0pt; font-size: 16pt;\"><span style=\"box-sizing: border-box; font-family: 仿宋_GB2312;\">执行中遇到的问题，请及时报告。</span></p><p style=\"box-sizing: border-box; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: justify; line-height: 20pt; text-indent: 31.6pt; margin: 0cm 0cm 0pt; font-size: 16pt;\">&nbsp;</p><p style=\"box-sizing: border-box; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: right; line-height: 25pt; text-indent: 268.5pt; margin: 0cm 0cm 0pt; font-size: 16pt;\"><span style=\"box-sizing: border-box; font-family: 仿宋_GB2312;\">安徽省公安厅</span></p><p style=\"box-sizing: border-box; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: right; line-height: 25pt; text-indent: 268.5pt; margin: 0cm 0cm 0pt; font-size: 16pt;\"><span style=\"box-sizing: border-box; font-family: 仿宋_GB2312;\"></span><span style=\"box-sizing: border-box;\">2017</span><span style=\"box-sizing: border-box; font-family: 仿宋_GB2312;\">年</span><span style=\"box-sizing: border-box;\">12</span><span style=\"box-sizing: border-box; font-family: 仿宋_GB2312;\">月</span><span style=\"box-sizing: border-box;\">4</span><span style=\"box-sizing: border-box; font-family: 仿宋_GB2312;\">日</span></p><p><br/></p>',NULL,NULL,'0',0,0,0,'1','2018-02-04 13:52:19','2018-02-04 13:52:19','0'),('af00e4f3-7553-4ec5-86e3-74b7ec7d369b','2','e89a7d96-d7b5-4840-8e0d-bbdcbb6d4b98','日常工作兢兢业业',NULL,'<p>						</p><p style=\"text-align: center;\"><img src=\"/media-data/ueditor/image/20180205/1517813859178091900.jpg\" title=\"1517813859178091900.jpg\" alt=\"jj3.jpg\"/></p><p>\r\n					</p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,1,0,'1','2018-02-05 10:32:51','2018-02-05 14:57:41','0'),('b666ba77-8cc7-4ce0-9c75-d833d79c074a','1','7ef804ac-a238-4277-a4e0-dd1e155c3751','我市2018年“博爱送万家”活动启动','','<p><br/></p><p>2月4日上午，我市2018年“博爱送万家”活动正式启动，首批慰问品发放至50户困难群众手中。</p><p>“博爱送万家”活动是红十字会践行人道主义救助的重要内容。此次活动启动前夕，我市提前谋划，积极向省红十字会争取慰问物资200箱，包括大米、面粉、食用油、棉被、糖果、春联等物品，同时自筹资金购置了500箱“博爱送万家”物资，集中救助低保户、无保户、重病患者家庭、计划生育失独家庭、残疾人困难户等。</p><p>副市长马少华出席启动仪式并讲话。</p><p style=\"text-align: right;\">（记者王燕）</p><p><br/></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,0,0,'1','2018-02-05 20:14:47','2018-02-05 20:14:47','0'),('b6d8296b-d93d-4a9c-abee-806451ae9125','1','6bea8a78-4c27-45c5-92f7-cef4679d2479','关于召开道路交通安全会议的通知','','<p style=\"margin-top: 0px; margin-bottom: 0px; margin-left: 0pt; padding: 0px; color: rgb(51, 51, 51); font-family: 微软雅黑, 宋体, 黑体, &quot;Helvetica Neue&quot;, Tahoma, Arial, &quot;Hiragino Sans GB&quot;; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">镇各道安领导小组成员、各道安成员单位负责人：</p><p style=\"margin-top: 0px; margin-bottom: 0px; margin-left: 0pt; padding: 0px; color: rgb(51, 51, 51); font-family: 微软雅黑, 宋体, 黑体, &quot;Helvetica Neue&quot;, Tahoma, Arial, &quot;Hiragino Sans GB&quot;; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">为加强道路交通安全学习，提高交通安全意识，召开道路交通安全会议，现将有关事项通知如下：</p><p style=\"margin-top: 0px; margin-bottom: 0px; margin-left: 0pt; padding: 0px; color: rgb(51, 51, 51); font-family: 微软雅黑, 宋体, 黑体, &quot;Helvetica Neue&quot;, Tahoma, Arial, &quot;Hiragino Sans GB&quot;; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">一、会议时间：2018年3月2日上午9:15，会期半天。</p><p style=\"margin-top: 0px; margin-bottom: 0px; margin-left: 0pt; padding: 0px; color: rgb(51, 51, 51); font-family: 微软雅黑, 宋体, 黑体, &quot;Helvetica Neue&quot;, Tahoma, Arial, &quot;Hiragino Sans GB&quot;; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">二、会议地点：镇六楼会议室。</p><p style=\"margin-top: 0px; margin-bottom: 0px; margin-left: 0pt; padding: 0px; color: rgb(51, 51, 51); font-family: 微软雅黑, 宋体, 黑体, &quot;Helvetica Neue&quot;, Tahoma, Arial, &quot;Hiragino Sans GB&quot;; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">三、会议内容</p><p style=\"margin-top: 0px; margin-bottom: 0px; margin-left: 0pt; padding: 0px; color: rgb(51, 51, 51); font-family: 微软雅黑, 宋体, 黑体, &quot;Helvetica Neue&quot;, Tahoma, Arial, &quot;Hiragino Sans GB&quot;; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">1、研判辖区道安工作；</p><p style=\"margin-top: 0px; margin-bottom: 0px; margin-left: 0pt; padding: 0px; color: rgb(51, 51, 51); font-family: 微软雅黑, 宋体, 黑体, &quot;Helvetica Neue&quot;, Tahoma, Arial, &quot;Hiragino Sans GB&quot;; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">2、布置辖区道路安全整治工作；</p><p style=\"margin-top: 0px; margin-bottom: 0px; margin-left: 0pt; padding: 0px; color: rgb(51, 51, 51); font-family: 微软雅黑, 宋体, 黑体, &quot;Helvetica Neue&quot;, Tahoma, Arial, &quot;Hiragino Sans GB&quot;; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">3、布置黄标车清理工作；</p><p style=\"margin-top: 0px; margin-bottom: 0px; margin-left: 0pt; padding: 0px; color: rgb(51, 51, 51); font-family: 微软雅黑, 宋体, 黑体, &quot;Helvetica Neue&quot;, Tahoma, Arial, &quot;Hiragino Sans GB&quot;; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">4、布置年终考核工作。</p><p style=\"margin-top: 0px; margin-bottom: 0px; margin-left: 0pt; padding: 0px; color: rgb(51, 51, 51); font-family: 微软雅黑, 宋体, 黑体, &quot;Helvetica Neue&quot;, Tahoma, Arial, &quot;Hiragino Sans GB&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: right; text-indent: 2em;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; margin-left: 0pt; padding: 0px; color: rgb(51, 51, 51); font-family: 微软雅黑, 宋体, 黑体, &quot;Helvetica Neue&quot;, Tahoma, Arial, &quot;Hiragino Sans GB&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: right; text-indent: 2em;\">江山镇人民政府&nbsp;&nbsp;&nbsp;&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: 微软雅黑, 宋体, 黑体, &quot;Helvetica Neue&quot;, Tahoma, Arial, &quot;Hiragino Sans GB&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: right; text-indent: 2em;\">2018年2月18日</p><p><br/></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,0,0,'1','2018-02-04 11:07:43','2018-02-04 11:07:43','0'),('c78b466b-dd9a-4442-ae63-7903aa7d979c','2','e89a7d96-d7b5-4840-8e0d-bbdcbb6d4b98','整装待发备战高考',NULL,'<p>						</p><p style=\"text-align: center;\"><img src=\"/media-data/ueditor/image/20180205/1517813836400041819.jpg\" title=\"1517813836400041819.jpg\" alt=\"jj5.jpg\"/></p><p>\r\n					</p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,0,0,'1','2018-02-05 10:38:25','2018-02-05 14:57:18','0'),('c871e527-cd94-4e8e-9a1b-c6ac56fafbaa','1','83fd3625-2407-4cab-967e-1277112e7d51','安全驾驶跟车距离和跟车方法','','<p><span style=\"font-family: monospace; font-size: medium; white-space: pre-wrap;\">道路交通肇事中，很多是因为驾驶员跟车方法不当，或不能保持合适的跟车距离而造成的。因此，掌握正确的跟车方法，保持合适的跟车距离，对减少事故有重要意义。</span></p><p><span style=\"font-family: monospace; font-size: medium; white-space: pre-wrap;\">车辆的跟车驾驶特性表明：在同向行驶的一列汽车中，后车跟随前车运行，驾驶员总不愿意落后很多，总是紧随前车。这是一种&amp;ldquo;紧随要求&amp;rdquo;。因此，正确的跟车方法，是既要保证行车安全，又不致影响道路的交通容量。行车实践证明，为保证行车安全，跟车距离不得小于安全间距。</span></p><p><span style=\"font-family: monospace; font-size: medium; white-space: pre-wrap;\">驾驶员在跟车过程中，还要注意以下几个方面的问题。</span></p><p><span style=\"font-family: monospace; font-size: medium; white-space: pre-wrap;\">跟车时精力要高度集中，使自己所驾车辆在前车任何情况下停车时，也能及时停下来，即使前车制动灯不亮，或突然横滑、甩尾时，也能从容应付。</span></p><p><span style=\"font-family: monospace; font-size: medium; white-space: pre-wrap;\">在所驾车辆制动效果不佳，或货车载人，或运送大件物品时，应适当加大跟车距离，以留有足够的安全系数。</span></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,0,0,'1','2018-02-05 21:06:39','2018-02-05 21:06:39','0'),('cda3413d-4e2d-48df-acb3-3e86c39bcf61','1','0ccc3466-2f38-4640-ad22-92fced9f45ba','美丽女警” 我们为自己代言','','<p><br/></p><p>&nbsp;不久前，在微博上发起了“美丽女警”的相关话题，你可以在微博上发表你心目中的美丽女警！我不知道别人对“美丽女警”的标准在哪里，但对我来说觉得自己身边的每一位女警，都有着她自己的个性和光彩，都是如此朴素又绚丽。</p><p>&nbsp; &nbsp; 2012年，“美丽”被赋予了更深层次的含义，不断出现在大众视野中，“最美丽的老师”、“最美丽的司机”等等普通却又伟大的小人物感动着大家，他们的职业很普通，他们的生活很普通，但他们在生命攸关的那一刹那，无需思考而本能的反应凸显出了他们人格的崇高。我不喜欢，一个英雄的诞生需要用他自己的生命来诠释，更不愿看到一个活着的英雄在一时的喧嚣灿烂之后面对的是现实生活的残忍。我喜欢小人物的普通和平凡，更欣赏小人物尽职尽责、默默付出而构建的社会安定团结。我旁边的女警们就是这个样子，不嗔不噪，默默奉献，在工作和生活中尽心尽力地扮演好自己的角色。</p><p>&nbsp; &nbsp; 工作中，她们在各自的岗位上履行着各自的职责，交警的首个女子岗亭让人眼前一亮，她的美丽，她的严厉，总让我经过后不停地回眸，她会搀扶着老人家过斑马线，她会与不守交规的人面红耳赤，其实她也只是刚参加公安工作不久的小姑娘；在各个窗口接待群众的女警们，大家更是熟悉了，虽然繁忙的工作让她们无法顾及到每一位来办事的群众，但请相信她们都在努力践行着便民服务的理念；与犯罪分子打交道的女警们，她们以巾帼不让须眉之姿，凭着女性特有的细致入微和耐心韧劲帮助打击犯罪分子；还有其他各警种，都有着女警们的飒爽英姿。谁说女子不如男！殊不知，女警犹如一道彩虹，绚丽了公安蔚蓝的天空。</p><p>&nbsp; &nbsp; 生活中，她们也是父母捧在手心中的宝贝、家中贤淑的妻子和孩子的妈。流行时尚也是她们的最爱，淘宝网也是最常点击的网站，但因警察队伍的纪律性使她们新衣服买了没时间穿，总感慨着又浪费钱压箱底了；化妆品买了没地方用，总是放在柜子里等着过期。曾经也是娇娇女的她们，却要面对形形色色的人，不管是接待群众还是打击犯罪，她们都以不输男子的担当来勇敢面对。有家了，她们在繁忙的工作之余还要扮演着为人妻、为人母的角色，嘴上最常说也是“我那口子，我家的孩子……”，工作闲暇时也交流交流彼此的妈妈经，笑着述说着自家孩子的淘气调皮。不管她们在生活中扮演着何种角色，乖巧的女儿、温柔的妻子或是和蔼可亲的妈妈，在工作中的她们都会用以铿锵玫瑰的姿态来演绎着女警的风采。</p><p>&nbsp; &nbsp; 套用曾经流行的陈欧体来做个结束语：你只看我身穿警服英姿飒爽，却没看到我挑灯夜战熬夜加班，你有你的羡慕，我有我的心酸。你否定我的工作能力，我决定我的工作方式。你可以嘲笑我的不值，何必辛苦，我说你不懂这是一份爱。你可以轻视我的选择，我会证明，这是个需要女警的时代。女警工作注定是段艰辛的旅程，路上少不了家人的埋怨，但那又怎样，哪怕没人理解，也要干得出彩。我们是女警，我们为自己代言！</p><p><br/></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,1,0,'1','2018-02-05 19:39:16','2018-02-05 19:39:16','0'),('d5374169-d832-47f4-a137-ef832d7652b2','1','716702de-4c2f-4291-995e-b8882a8148c0','公安部长对全国公安厅局长下达改革“总攻令”','','<p><br/></p><p>【导读】全国公安厅局长座谈会7月11日在贵阳召开，国务委员、公安部党委书记、部长郭声琨出席会议并讲话，他强调要认真学习贯彻习近平总书记关于司法体制改革的重要指示精神，按照孟建柱同志在全国司法体制改革推进会上的部署要求，牢固树立“四个意识”，牢牢把握对党忠诚、服务人民、执法公正、纪律严明总要求，以只争朝夕的精神坚定不移推进公安改革，以担当务实的作风切实做好维护国家安全和社会稳定各项工作，努力以党和人民满意的优异成绩迎接党的十九大胜利召开。</p><p><br/></p><p>郭声琨强调，习近平总书记在会见全国公安系统英雄模范立功集体表彰大会代表时的重要讲话精神是引领新形势下公安事业发展进步的科学理论和行动指南。要不断深入学习宣传贯彻习近平总书记重要讲话精神，坚持不懈地用习近平总书记重要讲话精神武装头脑、指导实践、推动工作。要在抓“深”上下功夫，学深悟透精神实质、准确把握核心要义，不断深化认识、凝聚共识，切实把全警的思想和行动统一到习近平总书记重要讲话精神上来。要在抓“长”上下功夫，紧密结合推进“两学一做”学习教育常态化制度化，采取有力措施，坚持长期学习，不断推动学习宣传贯彻工作常态化制度化，推动对党忠诚、服务人民、执法公正、纪律严明的总要求进法律、进教材、进课堂，使之成为全警铭刻于灵魂、熔铸于血脉的坚定信念，将其作为建警治警的总方略、立警从警的座右铭在全警高高树立起来、坚决落实下去。要在抓“实”上下功夫，付诸于实践、见之于实效，切实把学习贯彻成果转化为维护国家安全和社会稳定的实际行动，转化为全面推进公安改革和“四项建设”的具体实践，转化为全警干事创业和创先争优的不懈动力，不断开创公安工作新局面。</p><p>郭声琨指出，积极适应信息化发展大势，紧紧抓住新一轮科技革命的历史机遇，因势而变、把握主动，关系着公安工作的未来。要以时不我待的时代使命感和紧迫感主动拥抱大数据、云计算、人工智能新时代，毫不动摇地推进改革强警战略，大力推进警务机制改革与现代科技应用深度融合，不断提升打击犯罪的能力和水平。要深入推进大数据建设，按照“大整合、高共享、深应用”的思路，坚持统一领导、统一规划、统一标准，加大整合力度，避免重复建设，着力打破信息数据资源壁垒，广泛聚合共享内外数据资源，加快构建数据支撑、情报引领的打击犯罪新机制，加快推进现代科技手段的综合应用，不断增强侦查打击的精确性、主动性和时效性。</p><p>郭声琨要求，要大力推进大数据时代的社会治理创新，更加注重科学治理，充分运用大数据，努力实现对各类风险隐患的自动识别、敏锐感知和预测预警、预防预控，着力提升社会治理的能力水平。要更加注重源头治理，积极推动落实对新技术新领域新业态的监管责任和相关企业单位的主体责任，广泛动员人民群众参与，充分发挥社会组织作用，着力形成社会共治合力。要更加注重依法治理，坚持不懈深化公安执法规范化建设，不断提升公安机关依法治理能力和执法公信力。</p><p>郭声琨要求，要大力推进各项“放管服”改革举措的落实，进一步提升服务发展、服务群众的能力水平。年内公安部关于简化优化服务流程方便群众办事创业的28项措施要全部落实到位。要力争到年底基本建成“互联网+公安政务服务”一体化网上政务服务平台，努力让信息多跑路、让群众少跑腿。要加快推进户籍制度改革，积极会同有关部门，推动完善配套政策措施，确保年内1300万人进城落户任务如期完成。要着力解决好无户口人员登记户口问题，坚决落实好居民身份证异地受理、挂失申报和丢失招领三项制度，努力让更多人享受改革红利。要坚决杜绝乱收滥罚现象，狠抓窗口单位规范化建设和规范派出所出具证明等意见的落实，切实解决好群众反映强烈的突出问题。要研究推出更多便民利民新举措，不断增添经济发展新动力、增强人民群众获得感。</p><p>郭声琨强调，要紧紧围绕为党的十九大胜利召开创造安全稳定社会环境的主题主线，从严从实从细抓好保稳定、护安全、促和谐各项工作的落实，全力以赴打赢党的十九大安保维稳这场硬仗。要始终坚持把维护政治安全置于首位，切实增强政治敏锐性，严密防范、坚决打击敌对势力的各种捣乱破坏活动。要牢牢绷紧反恐怖斗争这根弦，坚持情报先行、防范在前，主动进攻、露头就打，源头治理、整体防控，始终保持严打高压威慑态势，坚决把暴恐活动消灭在预谋阶段、摧毁在行动之前。要深入开展矛盾风险排查化解管控工作，坚持关口前移、源头防范，强化风险评估、应急处置，不断提高对各类矛盾风险的发现、化解、管控能力。</p><p>郭声琨指出，公安部党委决定，今年下半年将在全国公安机关开展以圆满完成党的十九大安保维稳任务为目标的创建活动，进一步突出重点、抓住关键，树立高标准、落实严要求，集中组织开展打击制贩枪支犯罪专项行动、集中开展易制爆危险物品和寄递物流安全管理专项整治行动、集中开展以高层建筑消防安全为重点的综合治理行动，并以此带动全面落实各项打防管控和社会治理措施，确保社会治安大局持续稳定。</p><p>郭声琨强调，做好党的十九大安保维稳工作，是对各级公安机关领导干部“四个意识”和担当精神的实际考验。要紧紧围绕创建活动，严格绩效考核评价，落实奖惩激励措施，强化督察问效追责，把工作落实情况纳入领导班子和领导干部绩效考核，对工作中表现突出的领导干部和民警个人予以通报表扬、记功奖励，对失职渎职的依纪依法严肃处理。各级公安领导干部要勇于担当、敢于负责，以身作则、率先垂范，坚持以坚强的党性抓落实、以务实的作风抓落实、以严明的纪律抓落实，确保各项工作措施落到实处、取得实效。</p><p><br/></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,1,0,'1','2018-02-05 20:20:13','2018-02-05 20:20:13','0'),('ddba524e-ec51-4a31-9d16-e83645a47425','1','85627160-b3a8-423f-8648-e2bdccbbacae','市领导主持召开市委常委会会议','/media-data/image/20180205154719/jj2.jpg','<p>						</p><p style=\"font-family: Arial, ����, Tahoma, sans-serif; font-size: 14px; line-height: 27px; margin-top: 15px; margin-bottom: 0px; padding: 0px; color: rgb(51, 51, 51); text-indent: 2em; white-space: normal; background-color: rgb(255, 255, 255);\">（记者 赵云涛）2月2日，市委书记潘朝晖主持召开市委常委会会议，深入学习贯彻习近平总书记在十九届中央纪委二次全会上的重要讲话精神，传达学习省纪委十届三次全会精神，讨论《市纪委十届三次全会工作报告》等。</p><p style=\"font-family: Arial, ����, Tahoma, sans-serif; font-size: 14px; line-height: 27px; margin-top: 15px; margin-bottom: 0px; padding: 0px; color: rgb(51, 51, 51); text-indent: 2em; white-space: normal; background-color: rgb(255, 255, 255);\">会议指出，习近平总书记在十九届中央纪委二次全会上的重要讲话，为新时代推进全面从严治党指明了前进方向、提供了根本遵循，具有极强的政治性、思想性、针对性。我们要认真学习领会，融会贯通地理解习近平总书记全面从严治党的重要思想，全面贯彻落实党的十九大战略部署，持之以恒、毫不动摇推动全面从严治党向纵深发展。</p><p style=\"font-family: Arial, ����, Tahoma, sans-serif; font-size: 14px; line-height: 27px; margin-top: 15px; margin-bottom: 0px; padding: 0px; color: rgb(51, 51, 51); text-indent: 2em; white-space: normal; background-color: rgb(255, 255, 255);\">会议强调，要提高政治站位，树牢“四个意识”，坚定“四个自信”，始终在政治立场、政治方向、政治原则、政治道路上同以习近平同志为核心的党中央保持高度一致。要充分认清全面从严治党面临的复杂严峻形势，全面推进党的政治建设、思想建设、组织建设、作风建设、纪律建设，把制度建设贯穿其中，深入推进反腐败斗争，以永远在路上的韧劲和执着推进全面从严治党。要持续加强作风建设，紧扣保持党同人民群众血肉联系这个关键，锲而不舍纠正“四风”，着力解决群众反映强烈的突出问题。要健全监督体系，深化国家监察体制改革，加强巡视整改督查，巩固和创新巡察工作，始终保持反腐败高压态势，着力强化不敢腐的震慑，扎牢不能腐的笼子，增强不想腐的自觉，通过不懈努力换来海晏河清、朗朗乾坤。</p><p style=\"font-family: Arial, ����, Tahoma, sans-serif; font-size: 14px; line-height: 27px; margin-top: 15px; margin-bottom: 0px; padding: 0px; color: rgb(51, 51, 51); text-indent: 2em; white-space: normal; background-color: rgb(255, 255, 255);\">会议还研究了其他事项。</p><p><br/></p><p>\r\n					</p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,5,0,'1','2018-02-03 22:26:05','2018-02-05 15:47:21','1'),('e3e2daae-2e2d-4f23-8f60-7aa04a26f447','1','dec65f55-feb3-4ef4-909d-1022261285fc','郭声琨会见英国首相国家安全顾问塞德维尔','','<p style=\"font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center; text-indent: 2em; margin-bottom: 1em; font-size: 18px;\"><span style=\"font-family: &quot;Times New Roman&quot;;\">1</span>月<span style=\"font-family: &quot;Times New Roman&quot;;\">11</span>日，中共中央政治局委员、中央政法委书记郭声琨在北京会见英国首相国家安全顾问塞德维尔。</p><p style=\"font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; margin-bottom: 1em; font-size: 18px;\">&nbsp;&nbsp;&nbsp;</p><p style=\"font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; margin-bottom: 1em; font-size: 18px;\">中共中央政治局委员、中央政法委书记郭声琨<span style=\"font-family: &quot;Times New Roman&quot;;\">1</span>月<span style=\"font-family: &quot;Times New Roman&quot;;\">11</span>日在京会见了英国首相国家安全顾问塞德维尔。</p><p style=\"font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; margin-bottom: 1em; font-size: 18px;\">郭声琨指出，中共十九大为实现中华民族伟大复兴的中国梦吹响了新的集结号，习近平总书记在十九大报告中再次强调推动构建人类命运共同体，这为加强中英各领域合作提供了新的历史机遇、指明了前进方向。希望双方执法安全部门抓住机遇，认真落实两国领导人共识，深化反恐、打击跨国有组织犯罪等合作，推动中英关系“黄金时代”深入发展。</p><p style=\"font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; margin-bottom: 1em; font-size: 18px;\">塞德维尔祝贺中共十九大圆满成功，表示愿同中方加强执法安全合作，努力取得更多成果。</p><p style=\"font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; margin-bottom: 1em; font-size: 18px;\">公安部部长赵克志、中央政法委秘书长汪永清、国家安全部部长陈文清、外交部部长助理李惠来参加会见。（图：郝帆）</p><p><br/></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,0,0,'1','2018-02-03 22:33:04','2018-02-03 22:33:04','0'),('e3e6474a-2856-4662-8d98-a733b7670f20','1','237cb6ba-265c-4f97-92b3-793914603388','李国英主持召开省政府第二次常务会议','','<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"margin: 0px; padding: 0px; list-style: none; font-family: KaiTi_GB2312;\"><strong style=\"margin: 0px; padding: 0px; list-style: none;\">研究《关于推进乡村振兴战略的实施意见》，审议通过关于进一步扩大和升级信息消费持续释放内需潜力的意见、采煤沉陷区综合治理规划、半导体﹙集成电路﹚产业发展规划、进一步加强控辍保学提高义务教育巩固水平等</strong></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255);\">　　2月3日上午，省长李国英主持召开省政府第2次常务会议，研究《关于推进乡村振兴战略的实施意见》，审议通过关于进一步扩大和升级信息消费持续释放内需潜力的意见、采煤沉陷区综合治理规划、半导体(集成电路)产业发展规划、进一步加强控辍保学提高义务教育巩固水平等。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255);\">　　会议强调，要牢牢把握乡村振兴战略这个新时代“三农”工作的总抓手，按照“产业兴旺、生态宜居、乡风文明、治理有效、生活富裕”的总要求，扎实推进农业提质、绿色家园、文化兴盛、乡村善治、民生筑本“五大行动”，深入谋划一批能落地、可实施、收效好的重大项目，健全财政投入保障机制，引导工商资本、技术、人才更多流向农村，推动农业全面升级、农村全面进步、农民全面发展。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255);\">　　会议指出，信息消费对拉动内需、引领产业升级至关重要。要补齐短板，加快升级城乡网络基础设施，夯实信息产业基础，突破信息消费发展瓶颈。要提升质量，在医疗、教育、养老、物流、农业、城市管理等领域，实施“智慧+”，适应高质量发展要求。要加强创新，推动互联网与工业设计、农业生产、流通销售等各领域深度融合，创新发展模式，打造信息消费升级版。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255);\">　　会议强调，推进采煤沉陷区综合治理，要创新治理模式，把治理和发展紧密结合起来，化害为利，系统治理，严格控制新生沉陷区，严格落实县(区)政府的综合治理直接主体责任，强化煤矿企业的治理责任。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255);\">　　会议指出，半导体产业是战略性、基础性和先导性产业。要提升战略定位，集中省市资源，充分发挥省“三重一创”专项资金和省集成电路产业投资基金作用，支持重点企业、重大项目建设，强化知识产权保护，抢占产业发展制高点。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: justify; white-space: normal; background-color: rgb(255, 255, 255);\">　　会议强调，要切实解决义务教育学生失学辍学问题，建立健全行政督促复学机制、部门联控联保工作机制和控辍保学动态监测机制等，加强督导与考核问责，确保各级政府履行法定职责，确保适龄儿童依法接受义务教育。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, 微软雅黑, 宋体, tahoma, Verdana, arial, sans-serif; text-align: right; white-space: normal; background-color: rgb(255, 255, 255);\">　　会议还研究了其他事项。（记者 吴量亮）</p><p><br/></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,0,0,'1','2018-02-05 20:47:57','2018-02-05 20:47:57','0'),('e43d59e5-9440-4518-a3b5-9c747beb32e6','1','85627160-b3a8-423f-8648-e2bdccbbacae','55名电信网络诈骗犯罪嫌疑人从印尼被押解回国','/media-data/image/20180205154641/jj.jpg','<p>						</p><p style=\"font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); margin-bottom: 1em; font-size: 18px; text-indent: 2em;\"><span style=\"font-family: &quot;Times New Roman&quot;;\">2</span>月<span style=\"font-family: &quot;Times New Roman&quot;;\">2</span>日<span style=\"font-family: &quot;Times New Roman&quot;;\">17</span>时许，随着一架中国民航包机降落在天津国际机场，<span style=\"font-family: &quot;Times New Roman&quot;;\">55</span>名涉嫌假冒公检法机关实施电信网络诈骗犯罪的嫌疑人被我公安机关从印尼押解回国，初步核破涉及全国<span style=\"font-family: &quot;Times New Roman&quot;;\">20</span>个省份的<span style=\"font-family: &quot;Times New Roman&quot;;\">60</span>余起案件，涉案金额达<span style=\"font-family: &quot;Times New Roman&quot;;\">3000</span>万元人民币。这是继去年<span style=\"font-family: &quot;Times New Roman&quot;;\">8</span>月我公安机关从印尼押解<span style=\"font-family: &quot;Times New Roman&quot;;\">143</span>名犯罪嫌疑人回国后，又一次成功从印尼将犯罪嫌疑人押解回国。</p><p style=\"font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); margin-bottom: 1em; font-size: 18px; text-indent: 2em;\"><span style=\"font-family: &quot;Times New Roman&quot;;\">2017</span>年<span style=\"font-family: &quot;Times New Roman&quot;;\">6</span>月，公安部派员率天津、湖南等地公安机关办案人员组成的工作组，赴印度尼西亚开展打击电信网络诈骗犯罪执法合作，共捣毁<span style=\"font-family: &quot;Times New Roman&quot;;\">5</span>个诈骗窝点，后将抓获的<span style=\"font-family: &quot;Times New Roman&quot;;\">143</span>名犯罪嫌疑人押解回国。此次行动后，印尼警方根据我方提供的新线索，继续开展有关案件侦办工作，并在掌握大量线索和犯罪证据的基础上，于今年<span style=\"font-family: &quot;Times New Roman&quot;;\">1</span>月<span style=\"font-family: &quot;Times New Roman&quot;;\">11</span>日再次行动，捣毁藏匿在巴厘岛的<span style=\"font-family: &quot;Times New Roman&quot;;\">3</span>个诈骗窝点，抓获嫌疑人<span style=\"font-family: &quot;Times New Roman&quot;;\">55</span>名。<span style=\"font-family: &quot;Times New Roman&quot;;\">1</span>月<span style=\"font-family: &quot;Times New Roman&quot;;\">16</span>日，公安部紧急派出工作组赴印尼，开展案件后续处置及调查取证工作。近日，在我驻印尼使领馆的大力支持下，印尼有关部门决定将<span style=\"font-family: &quot;Times New Roman&quot;;\">55</span>名犯罪嫌疑人移交我方处理。公安部指定由天津公安机关组织民警实施押解并办理相关案件。</p><p style=\"font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); margin-bottom: 1em; font-size: 18px; text-indent: 2em;\">公安部有关负责人表示，冒充公检法机关等方式实施的电信网络诈骗犯罪，严重侵害群众财产权益，影响群众安全感，影响社会秩序。公安机关将始终保持对电信网络诈骗犯罪的高压严打态势，不断加强国际执法合作，坚决把犯罪分子绳之以法，切实维护人民群众财产安全和合法权益。</p><p><br/></p><p>\r\n					</p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,1,0,'1','2018-02-03 22:26:43','2018-02-05 15:46:43','1'),('e569c949-b044-42e4-b42d-afef654f84a0','1','0baa5f76-e9e5-40a0-8b76-0223681fe1d0','交警一大队召开事故工作预防会议','','<p><span style=\"font-family: simsun; font-size: 14px; background-color: rgb(255, 255, 255);\">为有效预防重特大交通事故的发生，确保辖区道路交通安全形势保持平稳，12月11日，大队召开中层干部会议，专题研究事故预防工作。</span></p><p><span style=\"font-family: simsun; font-size: 14px; background-color: rgb(255, 255, 255);\">会上，大队长首先通报了近期全国连续发生的几起重特大道路交通事故，分析当前交通安全形势，针对近期交通事故频发多发势头，要求各中队切实落实各项交通安全管理措施，全力以赴确保冬季道路交通安全。</span><br/><span style=\"font-family: simsun; font-size: 14px; background-color: rgb(255, 255, 255);\">&nbsp; &nbsp; 最后，大队要求各中队负责人认真汲取近期全国连续发生的重特大道路交通事故教训，切实提高对当前交通事故预防工作严峻形势的认识，各分管领导要深入一线，率先垂范，靠前指挥；各中队要严格落实24小时巡逻制度，确保各辖区路面不失控，要立即对辖区客车、危化车、校车、施工车进行一次全面排查，对存在安全隐患的车辆要立即责令整改。各中队要对校车、危险品运输车、7座以上客运车辆要逐车登记检查，逐车录入重点车辆核查系统，严厉打击农用车违法载人。同时，努力提高路面见警率及管事率，严厉打击酒驾、涉牌涉证等交通违法行为，全力预防重特大道路交通事故的发生，为辖区创造安全、有序的道路交通环境。</span></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,0,0,'1','2018-02-04 14:13:44','2018-02-04 14:13:44','0'),('e82489e8-4830-4dc5-915c-c81118c17fda','1','85627160-b3a8-423f-8648-e2bdccbbacae','春运期间安徽高速10条路段易拥堵','','<p style=\"padding: 0px; margin-top: 0px; margin-bottom: 18px; border: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); line-height: 34px; text-indent: 16px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">为期40天的春运已经开始。据预测，今年全省春运旅客发送量预计为7864万人次，其中道路5904万人次，占75.08%。省公安交警总队根据预测，发布了今年春运期间我省高速公路10条易拥堵路段。</p><p style=\"padding: 0px; margin-top: 0px; margin-bottom: 18px; border: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); line-height: 34px; text-indent: 16px; font-family: 微软雅黑; white-space: normal; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;这10条易拥堵路段是，G3京台高速(黄山区—汤口段)1267km、1299km，铜陵、黄山双向；G36宁洛高速(曹庄—大刘郢枢纽)44km、301km，节前洛阳方向，节后南京方向；G40沪陕高速(吴庄—陇西枢纽)512km、596km，节前合肥方向，节后南京方向；G40沪陕高速(新桥—合肥西)663km、654km，节前六安方向，节后南京方向；G4001沪陕高速(徐集—六安北)718km、698km，节前河南方向，节后合肥方向；G4001合肥绕城南环(包河大道—小西冲枢纽)30km、33km，双向；G4212合安高速(严店—舒城—马堰枢纽)1060km、1101km，双向；G50沪渝高速(皖浙道口—宣城东枢纽)202km、269km，节前芜湖方向，节后浙江方向；G5011芜合高速(雍镇—芜湖长江大桥)23km、11km，双向；G5011芜合高速(陇西枢纽—马鞍山西枢纽)116km、58km，节前合肥方向，节后苏浙方向。</p><p><br/></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,0,0,'1','2018-02-05 17:03:34','2018-02-05 17:03:34','0'),('effb9c65-e16f-4ef9-9d7d-c523f10b91be','1','0ccc3466-2f38-4640-ad22-92fced9f45ba','处  警','','<p><br/></p><p>一首熟悉的手机音乐响起，将睡梦中的我吵醒了，眼睛都还没睁开，就下意识接起了电话。教导员：“小王，接个警，有个人喝醉酒躺在菜场附近，具体位置你到了再找一下，我们去送对象了，这事就交给你了。”</p><p>&nbsp; &nbsp; 一看时间，深夜12点。</p><p>&nbsp; &nbsp; 我一骨碌地坐起来，让我去处警？处警！所有睡梦中的迷糊瞬间散去，无比清晰地知道这么一个事实——处警，而且是我一个女内勤单枪匹马地去处警。所里已经没其他警力了，送对象的送对象，办案的都办案去了，只剩下我这个平时在他们眼里“不算警力”的女民警。</p><p>&nbsp; &nbsp; 既担忧又兴奋。担忧的是如果醉汉发起酒疯来，我该怎么办？虽然之前跟他们出过几次赌博的警，但这一次是自己第一次“当主角”处警。见过很多醉酒的，但处警却没经验；兴奋的是，让我挑大梁接处警，这第一次的经历弥足珍贵。</p><p>&nbsp; &nbsp; 警情就是命令，迅速带着协警出警了，还问了师兄如何处理这种警。</p><p>&nbsp; &nbsp; 凌晨的夜里，细雨飘飘，凉风很袭人。</p><p>&nbsp; &nbsp; 凌晨的夜里，警灯闪烁，我们在寻人。</p><p>&nbsp; &nbsp; 没多久，我们在一家未关门的小店门口找到了醉汉，店长报的警，跟我们说了大概经过：这个醉汉是当地人，至于哪个村店长也不知道，只见醉汉摇摇晃晃从小巷子里走出来，在他店门口吐了，然后就直接倒地睡着了。</p><p>&nbsp; &nbsp; 我们检查了下醉汉，嘴边还挂着呕吐物，由于下着小雨，地面是潮湿的，浸湿了他的衣服，光着的脚板不知道在哪何时踩到了碎片，有个小伤口血冻住了。我们试图唤醒一点他的意识，起码能让我们知道他是谁，住在哪里，或者亲人的联系方式。结果，一无所获，对着个睡得痴痴的又不明身份的醉汉，感觉似乎无从下手。</p><p>&nbsp; &nbsp; 我朝小巷子里望望，路灯下那小巷子也许能给我们点答案，于是我提议到小巷子里看看，能不能找到什么人知道一点线索。果然，有个大嫂站在巷子的转角处，神情有些担忧，似乎在等我们一样，我们就上去询问了下。大嫂说，醉汉早前曾在她的小店里想买酒，她没给卖。还告诉了我们最重要的一条信息，她知道醉汉的家庭地址。</p><p>&nbsp; &nbsp; 太好了！当时我的心情一下子从焦虑化成了惊喜。我们请大嫂协助帮忙，大嫂也热情地答应给我们带路。再回到小店处时，周围已经站了三三两两的人，几个大叔帮忙一起把醉汉抬到车上，大嫂和一位大叔很热情地跟我们一起将醉汉送回去。</p><p>&nbsp; &nbsp; 喊门喊了很久，终于睡在他家的工人下来开门了，几个男工人去抬人，大嫂则去找醉汉的亲人。我巡视了一下，面向路面的那一面全是空的没安装玻璃窗，一楼放了很多沙发，他家是加工沙发的。思量下，我跟他们几个说：“就把人放在一楼的沙发上睡吧，他家楼梯没扶手，楼梯转弯处没安玻璃，要是他醒了，酒还没过，迷迷糊糊地一不小心就容易出事。”工人告诉我们醉汉经常这样，今天也是喝了酒跟他老婆吵架，他老婆都离家出走了，家里亲戚都不管他了。把人安顿好，我特地找来平时不大管他的亲戚，叮嘱他们要好好照顾！</p><p>&nbsp; &nbsp; 回来的路上，那位大叔对我说道：“看你一个小姑娘，还挺心细的。”我(下转第44页)(上接第43页)知道他在指把醉汉放沙发上的事，我笑笑：“喝醉酒的人，看多了。”我突然想，一个人要做了什么才会妻离亲弃，他有两间半的房子，工人十多个，家里又是做加工沙发生意的，为何要把自己把家庭折腾成这样呢？真是家家有本难念的经！但喝酒解决不了问题。</p><p>&nbsp; &nbsp; 回所的时候已经凌晨1点多了，出来的时候太急没有拿伞，雨水打在身上，警服已经湿了。伴着深夜的凉风，有点冷。但一想到虽折腾了许久，却把醉汉安全送回了家，也小有成就，心里暖暖的。</p><p><br/></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,0,0,'1','2018-02-05 19:41:01','2018-02-05 19:41:01','0'),('ff9919b9-11df-432b-bfe2-9ae1937eeef1','1','f418246f-189b-4dc7-a6d1-b2155f56d23c','2017年部门预算编制说明','','<p><span style=\"color: rgb(85, 85, 85); font-family: 宋体, SimSun; font-size: 18px; background-color: rgb(255, 255, 255);\">&nbsp; &nbsp;按照预算管理有关规定，目前部门预算的编制实行综合预算制度，即全部收入和支出都反映在预算中。</span><span style=\"color: rgb(85, 85, 85); font-size: 18px; font-family: 宋体, SimSun;\">公安局交通管理大队预算安排支出主要用于保障该部门机构正常运转、完成日常工作任务以及承担道路交通交通发展相关工作。</span><span style=\"color: rgb(85, 85, 85); font-size: 18px; font-family: 宋体, SimSun;\">基本支出，是用于保障大、中队正常运转的日常支出，包括基本工资、津贴补贴等人员经费以及办公费、车辆燃油及维修费、印刷费、水电费、办公设备购置等日常公用经费。</span><span style=\"color: rgb(85, 85, 85); font-family: 宋体, SimSun; font-size: 18px;\">项目支出，是用于保障交管大队为完成特定的行政工作任务或事业发展目标，用于专项业务工作的经费支出。</span></p><p><br/></p>','7644cb53-5593-48f7-ba5b-39a99d4cc26f','d579e129-e328-46f7-880f-4e029264367c','0',0,1,0,'1','2018-02-04 14:05:31','2018-02-04 14:05:31','0');

