/*View structure for view v_article_info */

/*!50001 DROP TABLE IF EXISTS `v_article_info` */;
/*!50001 DROP VIEW IF EXISTS `v_article_info` */;

/*!50001 CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `v_article_info` AS select `j`.`id` AS `id`,`j`.`article_type` AS `article_type`,`j`.`topic_id` AS `topic_id`,`j`.`article_title` AS `article_title`,`j`.`article_image_url` AS `article_image_url`,`j`.`article_content` AS `article_content`,`j`.`publish_dept_id` AS `publish_dept_id`,`j`.`publish_user_id` AS `publish_user_id`,`j`.`check_state` AS `check_state`,`j`.`top_count` AS `top_count`,`j`.`view_count` AS `view_count`,`j`.`display_order` AS `display_order`,`j`.`data_state` AS `data_state`,`j`.`create_date` AS `create_date`,`j`.`update_date` AS `update_date`,`j`.`front_slider_state` AS `front_slider_state`,`j`.`topic_name` AS `topic_name`,`j`.`topic_code` AS `topic_code`,`j`.`content_image_url` AS `content_image_url`,`j`.`publish_dept_name` AS `publish_dept_name`,`k`.`user_name` AS `publish_user_name` from (((select `m`.`id` AS `id`,`m`.`article_type` AS `article_type`,`m`.`topic_id` AS `topic_id`,`m`.`article_title` AS `article_title`,`m`.`article_image_url` AS `article_image_url`,`m`.`article_content` AS `article_content`,`m`.`publish_dept_id` AS `publish_dept_id`,`m`.`publish_user_id` AS `publish_user_id`,`m`.`check_state` AS `check_state`,`m`.`top_count` AS `top_count`,`m`.`view_count` AS `view_count`,`m`.`display_order` AS `display_order`,`m`.`data_state` AS `data_state`,`m`.`create_date` AS `create_date`,`m`.`update_date` AS `update_date`,`m`.`front_slider_state` AS `front_slider_state`,`m`.`topic_name` AS `topic_name`,`m`.`topic_code` AS `topic_code`,`m`.`content_image_url` AS `content_image_url`,`n`.`dept_name` AS `publish_dept_name` from (((select `t`.`id` AS `id`,`t`.`article_type` AS `article_type`,`t`.`topic_id` AS `topic_id`,`t`.`article_title` AS `article_title`,`t`.`article_image_url` AS `article_image_url`,`t`.`article_content` AS `article_content`,`t`.`publish_dept_id` AS `publish_dept_id`,`t`.`publish_user_id` AS `publish_user_id`,`t`.`check_state` AS `check_state`,`t`.`top_count` AS `top_count`,`t`.`view_count` AS `view_count`,`t`.`display_order` AS `display_order`,`t`.`data_state` AS `data_state`,`t`.`create_date` AS `create_date`,`t`.`update_date` AS `update_date`,`t`.`front_slider_state` AS `front_slider_state`,`s`.`topic_name` AS `topic_name`,`s`.`topic_code` AS `topic_code`,substr(`t`.`article_content`,(locate('src="',`t`.`article_content`) + 5),58) AS `content_image_url` from (`bxs`.`t_article` `t` left join `bxs`.`t_topic` `s` on((`t`.`topic_id` = `s`.`id`))))) `m` left join `bxs`.`t_dept` `n` on((`m`.`publish_dept_id` = `n`.`id`))))) `j` left join `bxs`.`t_user` `k` on((`j`.`publish_user_id` = `k`.`id`))) */;

